						dt_contratacao,
						dt_rescisao,
						cd_motivo_rescisao,
						dt_compartilhamento,
						dt_fim_compartilhamento,
						ie_tipo_repasse)
					values (	nextval('pls_mov_benef_segurado_seq'),
						clock_timestamp(),
						current_setting('pls_mov_benef_pck.nm_usuario_w')::usuario.nm_usuario%type,
						clock_timestamp(),
						current_setting('pls_mov_benef_pck.nm_usuario_w')::usuario.nm_usuario%type,
						current_setting('pls_mov_benef_pck.pls_mov_benef_lote_w')::pls_mov_benef_lote%rowtype.nr_sequencia,
						tb_nr_seq_mov_operadora_w(i),
						tb_nr_seq_mov_contrato_w(i),
						tb_nr_seq_segurado_w(i),
						tb_cd_plano_intercambio_w(i),
						tb_cd_plano_origem_w(i),
						tb_nr_contrato_w(i),
						tb_cd_usuario_plano_w(i),
						tb_dt_validade_carteira_w(i),
						tb_nr_via_carteira_w(i),
						tb_nm_beneficiario_w(i),
						tb_nm_abreviado_w(i),
						tb_nm_social_w(i),
						tb_nm_social_abreviado_w(i),
						tb_dt_nascimento_w(i),
						tb_ie_sexo_w(i),
						tb_nr_cpf_w(i),
						tb_nr_identidade_w(i),
						tb_ds_orgao_emissor_ci_w(i),
						tb_sg_emissora_ci_w(i),
						tb_ie_estado_civil_w(i),
						tb_nm_mae_w(i),
						tb_nr_pis_pasep_w(i),
