						tb_cd_familia_w(i),
						tb_cd_matricula_estipulante_w(i),
						tb_ie_nascido_plano_w(i),
						tb_cd_dependencia_w(i),
						current_setting('pls_mov_benef_pck.tb_cd_titular_w')::pls_util_cta_pck.t_varchar2_table_50(i),
						current_setting('pls_mov_benef_pck.tb_dt_inclusao_operadora_w')::pls_util_cta_pck.t_date_table(i),
						current_setting('pls_mov_benef_pck.tb_dt_contratacao_w')::pls_util_cta_pck.t_date_table(i),
						current_setting('pls_mov_benef_pck.tb_dt_rescisao_w')::pls_util_cta_pck.t_date_table(i),
						current_setting('pls_mov_benef_pck.tb_cd_motivo_rescisao_w')::pls_util_cta_pck.t_varchar2_table_10(i),
						current_setting('pls_mov_benef_pck.tb_dt_compartilhamento_w')::pls_util_cta_pck.t_date_table(i),
