CREATE OR REPLACE PROCEDURE pls_mov_mens_pck.gerar_vencimentos ( nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

dt_vencimento_w		pls_mov_mens_operador_venc.dt_vencimento%type;
C01 CURSOR FOR
	SELECT	a.nr_sequencia nr_seq_mov_operadora,
		(	SELECT	sum(x.vl_item)
			from	pls_mov_mens_benef_item x,
				pls_mov_mens_benef y
			where	y.nr_sequencia	= x.nr_seq_mov_benef
			and	y.nr_seq_mov_operadora = a.nr_sequencia) vl_vencimento
	from	pls_mov_mens_operadora a
	where	a.nr_seq_lote	= current_setting('pls_mov_mens_pck.pls_mov_mens_lote_w')::pls_mov_mens_lote%rowtype.nr_sequencia;
BEGIN
begin
dt_vencimento_w	:= trunc(current_setting('pls_mov_mens_pck.pls_mov_mens_lote_w')::pls_mov_mens_lote%rowtype.dt_referencia,'month')+(current_setting('pls_mov_mens_pck.pls_mov_mens_regra_w')::pls_mov_mens_regra%rowtype.nr_dia_vencimento-1);
exception
when others then
	dt_vencimento_w	:= last_day(current_setting('pls_mov_mens_pck.pls_mov_mens_lote_w')::pls_mov_mens_lote%rowtype.dt_referencia);
end;
for r_c01_w in C01 loop
	begin
	insert	into	pls_mov_mens_operador_venc(	nr_sequencia, dt_atualizacao, nm_usuario,
			dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_mov_operadora,
			dt_vencimento, vl_vencimento )
		values (	nextval('pls_mov_mens_operador_venc_seq'), clock_timestamp(), nm_usuario_p,
			clock_timestamp(), nm_usuario_p, r_c01_w.nr_seq_mov_operadora,
			dt_vencimento_w, coalesce(r_c01_w.vl_vencimento, 0));
	end;
end loop;
END;

$body$
