CREATE OR REPLACE PROCEDURE pls_mov_mens_pck.inserir_mov_benef ( nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

C01 CURSOR FOR
	SELECT	c.nr_sequencia nr_seq_operadora,
		b.nr_seq_segurado,
		coalesce(pls_obter_dados_segurado(b.nr_seq_segurado, 'C'), '') cd_usuario_plano
	from	pls_mov_mens_temp		a,
		pls_mensalidade_segurado	b,
		pls_mov_mens_operadora		c,
		pls_mensalidade			d
	where	b.nr_sequencia		= a.nr_seq_mensalidade_seg
	and	c.nr_seq_congenere	= a.nr_seq_congenere
	and	d.nr_sequencia		= b.nr_seq_mensalidade
	and	c.nr_seq_lote		= current_setting('pls_mov_mens_pck.pls_mov_mens_lote_w')::pls_mov_mens_lote%rowtype.nr_sequencia
	and	coalesce(d.ie_cancelamento::text, '') = ''
	group by
		c.nr_sequencia,
		b.nr_seq_segurado;
BEGIN
for r_c01_w in C01 loop
	begin
	insert	into	pls_mov_mens_benef(	nr_sequencia, dt_atualizacao, nm_usuario,
			dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_lote,
			nr_seq_segurado, nr_seq_mov_operadora, cd_usuario_plano)
		values (	nextval('pls_mov_mens_benef_seq'), clock_timestamp(), nm_usuario_p,
			clock_timestamp(), nm_usuario_p, current_setting('pls_mov_mens_pck.pls_mov_mens_lote_w')::pls_mov_mens_lote%rowtype.nr_sequencia,
			r_c01_w.nr_seq_segurado, r_c01_w.nr_seq_operadora, r_c01_w.cd_usuario_plano);
	end;
end loop;
commit;

END;

$body$
