------------------------------------ VINCULAR BENEFICIÁRIO ------------------------------------
CREATE OR REPLACE PROCEDURE pls_mov_mens_receb_pck.vincular_beneficiario ( nr_seq_mov_benef_p pls_mov_mens_benef.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
BEGIN
if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	update	pls_mov_mens_benef
	set	nr_seq_segurado	= nr_seq_segurado_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_mov_benef_p;
	commit;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1077133); --Beneficiário não informado
end if;
END;

$body$
