SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_obj_plussoft_pck.aprovar_proposta ( nr_seq_proposta_p pls_proposta_adesao.nr_sequencia%type, nr_seq_motivo_cancel_p pls_motivo_cancelamento.nr_sequencia%type, cd_pessoa_usuario_p pessoa_fisica.cd_pessoa_fisica%type, ie_resultado_p INOUT bigint, ds_mensagem_erro_p INOUT text ) AS $body$
DECLARE


current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type			usuario.nm_usuario%type;


BEGIN
ie_resultado_p := 1;

--Encontra o usuario
begin
select	max(nm_usuario)
into STRICT	current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type
from	usuario
where	cd_pessoa_fisica = cd_pessoa_usuario_p
and	ie_situacao = 'A';
exception
when others then
	PERFORM set_config('pls_obj_plussoft_pck.nm_usuario_w', null, false);
end;

if (nr_seq_proposta_p IS NOT NULL AND nr_seq_proposta_p::text <> '') then
	begin
	--Motivo de cancelamento
	if (nr_seq_motivo_cancel_p IS NOT NULL AND nr_seq_motivo_cancel_p::text <> '') then
		update	pls_proposta_adesao
		set	nr_seq_motivo_cancel 	= nr_seq_motivo_cancel_p,
			nm_usuario 		= current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type,
			dt_atualizacao 		= clock_timestamp()
		where	nr_sequencia = nr_seq_proposta_p;
	end if;
	
	--salva alteracoes	
	commit;
	--aprovar proposta adesao
	CALL pls_aprovar_proposta(nr_seq_proposta_p, 'S', current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type);

	ie_resultado_p := 0;
	exception
	when others then
		--1 - divergencia
		ie_resultado_p		:= 1;
		ds_mensagem_erro_p	:= substr(wheb_mensagem_pck.get_texto(1110463) || ' ' || sqlerrm(SQLSTATE) ,1,255);
	end;
else
	--1 - divergencia
	ie_resultado_p		:= 1;
	ds_mensagem_erro_p	:= wheb_mensagem_pck.get_texto(1110456);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
