SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_obj_plussoft_pck.enc_beneficiario_carteirinha ( nr_seq_lote_p pls_lote_carteira.nr_sequencia%type, cd_pessoa_usuario_p pessoa_fisica.cd_pessoa_fisica%type, ds_mensagem_erro_p INOUT text) AS $body$
DECLARE


nr_seq_carteira_w		bigint;
ie_situacao_w			varchar(3);
current_setting('pls_obj_plussoft_pck.cd_estabelecimento_w')::smallint		smallint;
nr_seq_cart_emissao_w		bigint;
current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type			usuario.nm_usuario%type;

current_setting('pls_obj_plussoft_pck.c01')::CURSOR CURSOR FOR
	SELECT	nr_sequencia,
		coalesce(ie_situacao,'P')
	from  	pls_carteira_emissao
	where  	nr_seq_lote  = nr_seq_lote_p;


BEGIN
--Encontra o usuario
begin
select	max(nm_usuario)
into STRICT	current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type
from	usuario
where	cd_pessoa_fisica = cd_pessoa_usuario_p
and	ie_situacao = 'A';
exception
when others then
	PERFORM set_config('pls_obj_plussoft_pck.nm_usuario_w', null, false);
end;

begin
open current_setting('pls_obj_plussoft_pck.c01')::CURSOR;
loop
fetch current_setting('pls_obj_plussoft_pck.c01')::into CURSOR
	nr_seq_cart_emissao_w,
	ie_situacao_w;
EXIT WHEN NOT FOUND; /* apply on current_setting('pls_obj_plussoft_pck.c01')::CURSOR */
	begin
	if (ie_situacao_w	= 'P') then
		update	pls_carteira_emissao
		set	dt_recebimento 		= clock_timestamp(),
			dt_atualizacao 		= clock_timestamp(),
			nm_usuario_recebimento	= current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type,
			ie_situacao 		= 'D'
		where	nr_sequencia		= nr_seq_cart_emissao_w;

		select	a.nr_sequencia,
			c.cd_estabelecimento
		into STRICT	nr_seq_carteira_w,
			current_setting('pls_obj_plussoft_pck.cd_estabelecimento_w')::smallint
		from	pls_segurado_carteira a,
			pls_carteira_emissao b,
			pls_lote_carteira	c
		where	a.nr_sequencia	= b.nr_seq_seg_carteira
		and	c.nr_sequencia	= b.nr_seq_lote
		and	b.nr_sequencia	= nr_seq_cart_emissao_w;

		update	pls_segurado_carteira a
		set	a.ie_situacao	= 'D',
			nm_usuario = current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type,
			dt_atualizacao = clock_timestamp()
		where	a.nr_sequencia	= nr_seq_carteira_w;
		
		CALL pls_alterar_estagios_cartao(nr_seq_carteira_w,clock_timestamp(),'6',current_setting('pls_obj_plussoft_pck.cd_estabelecimento_w')::smallint,current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type);

	end if;
	end;
end loop;
close current_setting('pls_obj_plussoft_pck.c01')::CURSOR;
commit;
exception
when others then
	rollback;
	--Erro
	ds_mensagem_erro_p := substr(wheb_mensagem_pck.get_texto(1110482) || ' ' || sqlerrm(SQLSTATE) ,1,255);
end;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
