CREATE OR REPLACE PROCEDURE pls_obj_plussoft_pck.gerar_analise_adesao ( nr_seq_proposta_p pls_proposta_adesao.nr_sequencia%type, ie_envio_medico_p text, cd_pessoa_usuario_p pessoa_fisica.cd_pessoa_fisica%type, ie_resultado_p INOUT bigint, ds_mensagem_erro_p INOUT text ) AS $body$
DECLARE


current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type			usuario.nm_usuario%type;

--beneficiarios da proposta
current_setting('pls_obj_plussoft_pck.c01')::CURSOR CURSOR(	nr_seq_proposta_pc	pls_proposta_adesao.nr_sequencia%type) FOR
	SELECT	nr_sequencia
	from	pls_proposta_beneficiario
	where	nr_seq_proposta = nr_seq_proposta_pc;
BEGIN
ie_resultado_p := 1;

--Encontra o usuario
begin
select	max(nm_usuario)
into STRICT	current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type
from	usuario
where	cd_pessoa_fisica = cd_pessoa_usuario_p
and	ie_situacao = 'A';
exception
when others then
	PERFORM set_config('pls_obj_plussoft_pck.nm_usuario_w', null, false);
end;

if (nr_seq_proposta_p IS NOT NULL AND nr_seq_proposta_p::text <> '') then
	begin
	--Encaminhar para analise de adesao os beneficiarios
	if (ie_envio_medico_p = 'S') then
		--Envia beneficiarios da proposta para analise de adesao
		for r_c01_w in current_setting('pls_obj_plussoft_pck.c01')::CURSOR(nr_seq_proposta_p) loop
			CALL pls_gerar_analise_adesao(r_c01_w.nr_sequencia, current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type, 'S');
		end loop;
	end if;

	ie_resultado_p := 0;
	exception
	when others then
		--1 - divergencia
		ie_resultado_p		:= 1;
		ds_mensagem_erro_p	:= substr(wheb_mensagem_pck.get_texto(1110452) || ' ' || sqlerrm(SQLSTATE) ,1,255);
	end;
else
	--1 - divergencia
	ie_resultado_p		:= 1;
	ds_mensagem_erro_p	:= wheb_mensagem_pck.get_texto(1110456);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
