CREATE OR REPLACE PROCEDURE pls_obj_plussoft_pck.gerar_bonificacao_proposta ( nr_seq_proposta_p pls_proposta_adesao.nr_sequencia%type, nr_seq_bonificacao_p bigint, nm_usuario_p usuario.nm_usuario%type, ds_erro_p INOUT text) AS $body$
DECLARE

					
qt_bonificacao_proposta_w	bigint;
current_setting('pls_obj_plussoft_pck.ds_erro_w')::varchar(255)			varchar(255);
		

BEGIN

PERFORM set_config('pls_obj_plussoft_pck.ds_erro_w', null, false);

select	count(1)
into STRICT	qt_bonificacao_proposta_w
from	pls_bonificacao_vinculo
where	nr_seq_proposta = nr_seq_proposta_p
and	nr_seq_bonificacao = nr_seq_bonificacao_p;

if (qt_bonificacao_proposta_w = 0) then
	insert into	pls_bonificacao_vinculo(	nr_sequencia, nr_seq_bonificacao, nm_usuario,
			nm_usuario_nrec, dt_atualizacao, dt_atualizacao_nrec,
			nr_seq_proposta)
		values (	nextval('pls_bonificacao_vinculo_seq'), nr_seq_bonificacao_p, nm_usuario_p,
			nm_usuario_p, clock_timestamp(), clock_timestamp(),
			nr_seq_proposta_p);
else
	PERFORM set_config('pls_obj_plussoft_pck.ds_erro_w', wheb_mensagem_pck.get_texto(1110449), false);
end if;

ds_erro_p := current_setting('pls_obj_plussoft_pck.ds_erro_w')::varchar(255);

END;

