SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_obj_plussoft_pck.gerar_lote_mensalidade_plus ( nr_seq_contrato_p bigint, dt_mesano_referencia_p INOUT timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, ie_tipo_p text, nr_seq_lote_p INOUT bigint) AS $body$
DECLARE


nr_seq_lote_w		pls_lote_mensalidade.nr_sequencia%type;
dt_referencia_w		timestamp;
nr_contrato_w		bigint;
dt_inicio_adesao_w	pls_lote_mensalidade.dt_inicio_adesao%type;
dt_fim_adesao_w		pls_lote_mensalidade.dt_fim_adesao%type;


BEGIN

dt_referencia_w		:= trunc(dt_mesano_referencia_p,'month');

select	nr_contrato
into STRICT	nr_contrato_w
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_p;

if (ie_tipo_p = 'P') then
	dt_inicio_adesao_w := null;
	dt_fim_adesao_w := null;
elsif (ie_tipo_p = 'I') then
	dt_inicio_adesao_w	:= trunc(dt_mesano_referencia_p,'month');
	dt_fim_adesao_w := fim_mes(dt_mesano_referencia_p);
end if;

--Gerar sequencia do lote
select	nextval('pls_lote_mensalidade_seq')
into STRICT	nr_seq_lote_w	
;

--Insere lote de mensalidade
insert into pls_lote_mensalidade(	nr_sequencia, nm_usuario, dt_atualizacao,
		dt_mesano_referencia, ie_status, dt_geracao,
		ie_tipo_contratacao, ie_regulamentacao, ie_fator_moderador,
		ie_participacao, nr_seq_contrato, cd_estabelecimento,
		vl_lote, dt_liberacao, dt_geracao_titulos,
		ds_observacao, ie_primeira_mensalidade, nr_contrato,
		ie_tipo_lote,ie_pagador_beneficio_obito, cd_perfil,
		dt_inicio_adesao, dt_fim_adesao, nr_seq_segurado,
		ie_mensalidade_mes_anterior, ie_visualizar_portal, ie_cobrar_retroativo,
		ie_gerar_mensalidade_futura, ie_tipo_pessoa_pagador, ie_geracao_nota_titulo)
	values (nr_seq_lote_w, nm_usuario_p, clock_timestamp(),
		dt_referencia_w, '1', null,
		'T', 'T', 'T',
		'T', nr_seq_contrato_p, cd_estabelecimento_p,
		0, null, null,
		wheb_mensagem_pck.get_texto(1110475, 'NR_CONTRATO=' || nr_contrato_w), 'S', nr_contrato_w,
		'CO','N', wheb_usuario_pck.get_cd_perfil,
		dt_inicio_adesao_w, dt_fim_adesao_w, null,
		'N', 'N', 'N',
		'N', 'A', 'A');

commit;

nr_seq_lote_p := nr_seq_lote_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_obj_plussoft_pck.gerar_lote_mensalidade_plus ( nr_seq_contrato_p bigint, dt_mesano_referencia_p INOUT timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, ie_tipo_p text, nr_seq_lote_p INOUT bigint) FROM PUBLIC;
