SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_obj_plussoft_pck.gravar_beneficiario_proposta ( nr_seq_solicitacao_p INOUT bigint, nr_item_p INOUT bigint, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nr_titular_solic_p bigint, nr_seq_titular_contrato_p bigint, nr_seq_parentesco_p bigint, dt_nascimento_p timestamp, ie_nascido_plano_p text, nr_seq_regra_isencao_p bigint, ds_mensagem_erro_p INOUT text) AS $body$
DECLARE

nr_item_w			bigint;
nr_sequencia_w			bigint;
qt_pf_encontrada_w		integer;
qt_grau_parent_encontrado_w	integer;
ie_insere_registro_w		varchar(1) := 'S';
nm_usuario_w			usuario.nm_usuario%type;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	count(1)
	into STRICT	qt_pf_encontrada_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	
	if (qt_pf_encontrada_w = 0) then
		ie_insere_registro_w := 'N';
		ds_mensagem_erro_p := substr(wheb_mensagem_pck.get_texto(1110434, 'CD_PESSOA_FISICA=' || cd_pessoa_fisica_p),1,255);
	end if;
end if;

if (ie_insere_registro_w = 'S') then
	if (nr_seq_parentesco_p IS NOT NULL AND nr_seq_parentesco_p::text <> '') then
		select	count(1)
		into STRICT	qt_grau_parent_encontrado_w
		from	grau_parentesco
		where	nr_sequencia = nr_seq_parentesco_p;
		
		if (qt_grau_parent_encontrado_w = 0) then
			ie_insere_registro_w := 'N';
			ds_mensagem_erro_p := substr(wheb_mensagem_pck.get_texto(1110435, 'NR_SEQ_PARENTESCO=' || nr_seq_parentesco_p),1,255);
		end if;
	end if;
end if;

if (ie_insere_registro_w = 'S') then
	--Caso tenha sido informado Numero da solicitacao inserir com a mesma numeracao
	if (nr_seq_solicitacao_p IS NOT NULL AND nr_seq_solicitacao_p::text <> '') then
		begin
		select	coalesce(max(nr_item),0)+1
		into STRICT	nr_item_w
		from	w_plussoft_benef_proposta
		where	nr_sequencia = nr_seq_solicitacao_p;
		
		insert into w_plussoft_benef_proposta(		
				nr_sequencia,
				nr_item,
				cd_pessoa_fisica,
				nr_seq_parentesco,
				dt_nascimento,
				ie_nascido_plano,
				nr_seq_titular_contrato,
				nr_titular_solic,
				nr_seq_regra_carencia
				)
		values (		nr_seq_solicitacao_p,
				nr_item_w,
				cd_pessoa_fisica_p,
				nr_seq_parentesco_p,
				dt_nascimento_p,
				ie_nascido_plano_p,
				nr_seq_titular_contrato_p,
				nr_titular_solic_p,
				nr_seq_regra_isencao_p);
				
		commit;
		nr_item_p := nr_item_w;
		exception
		when others then
			ds_mensagem_erro_p := substr(wheb_mensagem_pck.get_texto(1110436) || ' ' || sqlerrm(SQLSTATE) ,1,255);
			rollback;
		end;
	--Caso contrario gera uma nova solicitacao
	else
		begin
		select	nextval('w_plussoft_benef_proposta_seq')
		into STRICT	nr_sequencia_w
		;
		
		delete	from w_plussoft_benef_proposta
		where	nr_sequencia = nr_sequencia_w;
		
		insert into w_plussoft_benef_proposta(		
				nr_sequencia,
				nr_item,
				cd_pessoa_fisica,
				nr_seq_parentesco,
				dt_nascimento,
				ie_nascido_plano,
				nr_seq_titular_contrato,
				nr_titular_solic,
				nr_seq_regra_carencia)
		values (		nr_sequencia_w,
				1,
				cd_pessoa_fisica_p,
				nr_seq_parentesco_p,
				dt_nascimento_p,
				ie_nascido_plano_p,
				nr_seq_titular_contrato_p,
				nr_titular_solic_p,
				nr_seq_regra_isencao_p);
				
		commit;
		exception
		when others then
			nr_sequencia_w := null;
			ds_mensagem_erro_p := substr(wheb_mensagem_pck.get_texto(1110436) || ' ' || sqlerrm(SQLSTATE) ,1,255);
			rollback;
		end;
		nr_seq_solicitacao_p := nr_sequencia_w;
		nr_item_p := 1;
	end if;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_obj_plussoft_pck.gravar_beneficiario_proposta ( nr_seq_solicitacao_p INOUT bigint, nr_item_p INOUT bigint, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nr_titular_solic_p bigint, nr_seq_titular_contrato_p bigint, nr_seq_parentesco_p bigint, dt_nascimento_p timestamp, ie_nascido_plano_p text, nr_seq_regra_isencao_p bigint, ds_mensagem_erro_p INOUT text) FROM PUBLIC;
