CREATE OR REPLACE PROCEDURE pls_obj_plussoft_pck.inserir_responsavel_contato ( cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_pessoa_juridica_estab_w	pessoa_juridica_estab.nr_sequencia%type;
qt_pessoa_fisica_w		bigint;


BEGIN

if (nm_contato_proposta_p IS NOT NULL AND nm_contato_proposta_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_pessoa_juridica_estab_w
	from	pessoa_juridica_estab
	where	cd_estabelecimento = cd_estabelecimento_p
	and	cd_cgc = cd_cgc_estipulante_p;
	
	if (nr_seq_pessoa_juridica_estab_w IS NOT NULL AND nr_seq_pessoa_juridica_estab_w::text <> '') then
		update	pessoa_juridica_estab
		set	nm_pessoa_contato = substr(nm_contato_proposta_p, 1,255),
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_seq_pessoa_juridica_estab_w;
	else
		if	(nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '' AND cd_cgc_estipulante_p IS NOT NULL AND cd_cgc_estipulante_p::text <> '') then
			insert into pessoa_juridica_estab(nr_sequencia, cd_cgc, cd_estabelecimento,
				dt_atualizacao, dt_atualizacao_nrec, nm_usuario,
				nm_usuario_nrec, ie_conta_dif_nf, nm_pessoa_contato)
			values (nextval('pessoa_juridica_estab_seq'), cd_cgc_estipulante_p, cd_estabelecimento_p,
				clock_timestamp(), clock_timestamp(), nm_usuario_p,
				nm_usuario_p, 'N', substr(nm_contato_proposta_p, 1,255));
		end if;
	end if;
end if;

if (cd_pessoa_responsavel_p IS NOT NULL AND cd_pessoa_responsavel_p::text <> '') then
	select	count(1)
	into STRICT	qt_pessoa_fisica_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_responsavel_p;
	
	if	((qt_pessoa_fisica_w > 0) and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (cd_cgc_estipulante_p IS NOT NULL AND cd_cgc_estipulante_p::text <> '')) then
		insert into pessoa_jur_resp(nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
			nm_usuario, nm_usuario_nrec, cd_cgc,
			ie_resp_cadastro, cd_pessoa_fisica)
		values (nextval('pessoa_jur_resp_seq'), clock_timestamp(), clock_timestamp(),
			nm_usuario_p, nm_usuario_p, cd_cgc_estipulante_p,
			'S', cd_pessoa_responsavel_p);	
	end if;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
