CREATE OR REPLACE PROCEDURE pls_obj_plussoft_pck.liberar_inconsistencia ( nr_seq_proposta_p pls_proposta_adesao.nr_sequencia%type, nr_seq_inconsistencia_p pls_proposta_validacao.nr_sequencia%type, cd_pessoa_usuario_p text, cd_resultado_p INOUT bigint, ds_mensagem_erro_p INOUT text) AS $body$
DECLARE


current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type	usuario.nm_usuario%type;
vl_parametro_w	varchar(2);


BEGIN
--Encontra o usuario
begin
select	max(nm_usuario)
into STRICT	current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type
from	usuario
where	cd_pessoa_fisica = cd_pessoa_usuario_p
and	ie_situacao = 'A';
exception
when others then
	PERFORM set_config('pls_obj_plussoft_pck.nm_usuario_w', null, false);
end;

--Rotina de liberacao de inconsistencia
begin
Obter_Param_Usuario(1232, 7, obter_perfil_ativo, current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type, wheb_usuario_pck.get_cd_estabelecimento, vl_parametro_w);
if (vl_parametro_w = 'S') then
	CALL pls_proposta_liberar_inco(	nr_seq_inconsistencia_p, current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type);
	--Retorno ok
	cd_resultado_p := 0;
else
	cd_resultado_p := 1;
	ds_mensagem_erro_p := wheb_mensagem_pck.get_texto(1110455);

end if;

exception
when others then
	--Com divergencia
	cd_resultado_p := 1;
	--captura excecao gerada
	ds_mensagem_erro_p := substr(sqlerrm(SQLSTATE),1,255);
end;

END;

$body$
