SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_obj_plussoft_pck.mudar_status_lote ( nr_seq_lote_p pls_lote_carteira.nr_sequencia%type, cd_pessoa_usuario_p pessoa_fisica.cd_pessoa_fisica%type, nm_status_p INOUT text, ie_resultado_p INOUT bigint, ds_mensagem_erro_p INOUT text) AS $body$
DECLARE


current_setting('pls_obj_plussoft_pck.cd_estabelecimento_w')::smallint	pls_contrato.cd_estabelecimento%type;
current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type		usuario.nm_usuario%type;
/*
ie_resultado_p
0 - OK
1 - Erro
*/
BEGIN
-- Erro
ie_resultado_p := 1;

--Encontra o usuario
begin
select	max(nm_usuario)
into STRICT	current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type
from	usuario
where	cd_pessoa_fisica = cd_pessoa_usuario_p
and	ie_situacao = 'A';
exception
when others then
	PERFORM set_config('pls_obj_plussoft_pck.nm_usuario_w', null, false);
end;

begin
if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then
	
	select	cd_estabelecimento
	into STRICT	current_setting('pls_obj_plussoft_pck.cd_estabelecimento_w')::smallint
	from 	pls_lote_carteira
	where 	nr_sequencia = nr_seq_lote_p;
	
	update 	pls_lote_carteira
	set	ie_situacao 	= 'D',
		nm_usuario	= current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia 	= nr_seq_lote_p;
	
	CALL pls_alt_valid_cart_lote_venc(nr_seq_lote_p,current_setting('pls_obj_plussoft_pck.cd_estabelecimento_w')::smallint,'N',current_setting('pls_obj_plussoft_pck.nm_usuario_w')::usuario.nm_usuario%type);
	
	commit;
end if;
--Sucesso
ie_resultado_p := 0;
nm_status_p := wheb_mensagem_pck.get_texto(1110480);
exception
when others then
	rollback;
	--Erro
	ie_resultado_p := 1;
	nm_status_p := '';
	ds_mensagem_erro_p := substr(wheb_mensagem_pck.get_texto(1110481) || ' ' || sqlerrm(SQLSTATE) ,1,255);
end;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
