CREATE OR REPLACE FUNCTION pls_obj_plussoft_pck.obter_email_pf_pj ( cd_cgc_p text, cd_pessoa_fisica_p text ) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(4000);


BEGIN

if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then

	select 	max(ds_email)
	into STRICT	ds_retorno_w
	from 	pessoa_juridica_estab a
	where	a.cd_cgc = cd_cgc_p;

elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	
	select	max(a.ds_email)
	into STRICT	ds_retorno_w
	from	compl_pessoa_fisica a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	a.ie_tipo_complemento = 1;
end if;

return	ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
