SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_obj_plussoft_pck.obter_se_inadimplente ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nr_cpf_p pessoa_fisica.nr_cpf%type, ie_situacao_p INOUT bigint, ds_mensagem_p INOUT text) AS $body$
DECLARE


dt_sysdate_trunc_w	timestamp;
qt_inadimplente_w	integer;

-- Rotina ajustada com base na PLS_OBTER_DEBITOS_PF
BEGIN

dt_sysdate_trunc_w := trunc(clock_timestamp() - interval '9 days','dd'); -- 21/07/2017 : O Marcelo Bertoldo pediu que a quantidade de dias 9 fosse fixa para bater com as inconsistencias dinamicas que eles cadastraram em OPS - Proposta de adesao Eletronica / Cadastros / inconsistencias dinamicas
--Verifica pelo codigo
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	
	select	count(1)
	into STRICT	qt_inadimplente_w
	from	titulo_receber t
	where	t.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	t.ie_situacao = '1'
	and	t.dt_vencimento < dt_sysdate_trunc_w;
	
--Verifica pelo cpf
elsif (nr_cpf_p IS NOT NULL AND nr_cpf_p::text <> '') then

	select	count(1)
	into STRICT	qt_inadimplente_w
	from	titulo_receber 	t,
		pessoa_fisica 	p
	where	t.cd_pessoa_fisica = p.cd_pessoa_fisica
	and	p.nr_cpf = nr_cpf_p
	and	t.ie_situacao = '1'
	and	t.dt_vencimento < dt_sysdate_trunc_w;

end if;

if (qt_inadimplente_w > 0) then
--Inadimplente
	ie_situacao_p := 2;
	ds_mensagem_p := wheb_mensagem_pck.get_texto(1110416);
else
--Adimplente
	ie_situacao_p := 1;
	ds_mensagem_p := wheb_mensagem_pck.get_texto(1110423);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
