CREATE OR REPLACE FUNCTION pls_obj_plussoft_pck.pls_verifica_se_pagador ( cd_cgc_p pessoa_juridica.cd_cgc%type, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE

				
ds_retorno_w		varchar(255) := 'N';

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pls_contrato_pagador	a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	((coalesce(a.dt_rescisao::text, '') = '') or (a.dt_rescisao > clock_timestamp()));
elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pls_contrato_pagador	a
	where	a.cd_cgc = cd_pessoa_fisica_p
	and	((coalesce(a.dt_rescisao::text, '') = '') or (a.dt_rescisao > clock_timestamp()));
end if;

return ds_retorno_w;

END;

$body$
