CREATE OR REPLACE PROCEDURE pls_ocor_imp_pck.aplica_filtro_procedimento ( ie_considera_selecao_p INOUT boolean, ie_incidencia_selecao_regra_p text, ie_incidencia_selecao_filtro_p text, ie_processo_excecao_p text, ie_regra_excecao_p pls_oc_cta_combinada.ie_excecao%type, ie_filtro_excecao_p pls_oc_cta_filtro.ie_excecao%type, nr_seq_combinada_p pls_oc_cta_combinada.nr_sequencia%type, nr_seq_ocorrencia_p pls_ocorrencia.nr_sequencia%type, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type, nr_seq_filtro_p pls_oc_cta_filtro.nr_sequencia%type, nr_seq_lote_protocolo_p pls_protocolo_conta_imp.nr_seq_lote_protocolo%type, nr_seq_protocolo_p pls_protocolo_conta_imp.nr_sequencia%type, nr_seq_conta_p pls_conta_imp.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc_imp.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat_imp.nr_sequencia%type, dt_inicio_vigencia_p pls_oc_cta_combinada.dt_inicio_vigencia%type, dt_fim_vigencia_p pls_oc_cta_combinada.dt_fim_vigencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


_ora2pg_r RECORD;
--Variaveis do retorno do Cursor Dinamico

tb_seq_conta_w			pls_util_cta_pck.t_number_table;
tb_seq_conta_proc_w		pls_util_cta_pck.t_number_table;
tb_seq_conta_mat_w		pls_util_cta_pck.t_number_table;
tb_seq_selecao_w		pls_util_cta_pck.t_number_table;
tb_guia_referencia_w		pls_util_cta_pck.t_varchar2_table_20;
tb_seq_segurado_w		pls_util_cta_pck.t_number_table;
tb_dt_item_w			pls_util_cta_pck.t_date_table;
tb_dt_item_hora_ini_w		pls_util_cta_pck.t_date_table;
tb_dt_item_hora_fim_w		pls_util_cta_pck.t_date_table;
tb_ie_origem_proced_w		pls_util_cta_pck.t_number_table;
tb_cd_procedimento_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_material_w		pls_util_cta_pck.t_number_table;
tb_hr_inicial_w			pls_util_cta_pck.t_date_table;
tb_hr_final_w			pls_util_cta_pck.t_date_table;

--Variaveis para manipulaaao dos registros validos

tb_seq_conta_ger_w		pls_util_cta_pck.t_number_table;
tb_seq_conta_proc_ger_w		pls_util_cta_pck.t_number_table;
tb_seq_conta_mat_ger_w		pls_util_cta_pck.t_number_table;
tb_guia_referencia_ger_w	pls_util_cta_pck.t_varchar2_table_20;
tb_seq_segurado_ger_w		pls_util_cta_pck.t_number_table;
tb_dt_item_ger_w		pls_util_cta_pck.t_date_table;
tb_dt_item_hora_ini_ger_w	pls_util_cta_pck.t_date_table;
tb_dt_item_hora_fim_ger_w	pls_util_cta_pck.t_date_table;
tb_ie_origem_proced_ger_w	pls_util_cta_pck.t_number_table;
tb_cd_procedimento_ger_w	pls_util_cta_pck.t_number_table;
tb_nr_seq_material_ger_w	pls_util_cta_pck.t_number_table;
tb_seq_selecao_ger_w		pls_util_cta_pck.t_number_table;
nr_indice_w			integer;
ie_registro_valido_w		boolean;
					
dados_restricao_w	varchar(5000);
valor_bind_w		sql_pck.t_dado_bind;
ds_select_w		varchar(20000);
cursor_w		sql_pck.t_cursor;
ds_campos_w		varchar(2000);
ds_campos_mat_w		varchar(2000);
qt_filtro_processado_w	integer;

c_filtro CURSOR(	nr_seq_filtro_pc	pls_oc_cta_filtro.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia,
		a.cd_procedimento,
		a.cd_grupo_proc,
		a.cd_especialidade,
		a.cd_area_procedimento,
		a.nr_seq_estrutura,
		a.ie_tipo_desp_proc,
		a.nr_seq_grupo_rec,
		a.nr_seq_grupo_servico,
		a.ie_consistencia_valor,
		a.dt_dia_semana,
		a.ie_feriado,
		a.ie_tipo_feriado,
		pls_manipulacao_datas_pck.obter_hora_data_atual(a.hr_inicial) hr_inicial,
		pls_manipulacao_datas_pck.obter_hora_data_atual(a.hr_final) hr_final,
		coalesce(a.vl_minimo_item_imp, a.vl_minimo_item) vl_minimo_item,
		coalesce(a.vl_max_item_imp, a.vl_max_item) vl_max_item,
		a.ie_origem_proced,
		a.ie_via_acesso
	from	pls_oc_cta_filtro_proc a
	where	a.nr_seq_oc_cta_filtro	= nr_seq_filtro_pc;

BEGIN
-- gerencia a aplicaaao do filtro de procedimento


-- Se nao tiver informaaaes da regra nao sera possavel aplicar os filtros.

if (nr_seq_filtro_p IS NOT NULL AND nr_seq_filtro_p::text <> '') then

	qt_filtro_processado_w := 0;
	
	-- Abrir vetor com todas as combinaaaes de filtro da regra 

	for r_c_filtro in c_filtro(nr_seq_filtro_p) loop
		
		--Sa entra a primeira vez

		if (qt_filtro_processado_w = 0) then
			-- Atualizar o campo auxiliar que sera utilizado para sinalizar os registros que foram processados

			CALL CALL CALL CALL CALL pls_ocor_imp_pck.atualiza_campo_auxiliar(	'F', ie_processo_excecao_p,
							nr_id_transacao_p, nr_seq_filtro_p);
		end if;
		
		begin
			-- obtam a restriaao do procedimento

			SELECT * FROM pls_ocor_imp_pck.obter_restricao_procedimento(	r_c_filtro.cd_procedimento, r_c_filtro.ie_origem_proced, r_c_filtro.cd_area_procedimento, r_c_filtro.cd_especialidade, r_c_filtro.cd_grupo_proc, r_c_filtro.nr_seq_estrutura, r_c_filtro.ie_tipo_desp_proc, r_c_filtro.nr_seq_grupo_rec, r_c_filtro.nr_seq_grupo_servico, r_c_filtro.dt_dia_semana, r_c_filtro.ie_feriado, r_c_filtro.ie_tipo_feriado, r_c_filtro.hr_inicial, r_c_filtro.hr_final, r_c_filtro.vl_minimo_item, r_c_filtro.vl_max_item, r_c_filtro.ie_via_acesso, valor_bind_w, ds_campos_w, ds_campos_mat_w) INTO STRICT _ora2pg_r;
 valor_bind_w := _ora2pg_r.valor_bind_p; ds_campos_w := _ora2pg_r.ds_campos_p; ds_campos_mat_w := _ora2pg_r.ds_campos_mat_p;
								
			-- Montar o select padrao juntamente as restriaaes.						

			valor_bind_w := pls_ocor_imp_pck.obter_select_filtro(	ie_considera_selecao_p, ie_incidencia_selecao_regra_p, ie_incidencia_selecao_filtro_p, ie_processo_excecao_p, nr_seq_ocorrencia_p, nr_id_transacao_p, nr_seq_filtro_p, nr_seq_lote_protocolo_p, nr_seq_protocolo_p, nr_seq_conta_p, nr_seq_conta_proc_p, nr_seq_conta_mat_p, dt_inicio_vigencia_p, dt_fim_vigencia_p, ds_campos_w, null, dados_restricao_w, ds_campos_w, null, dados_restricao_w, ds_campos_mat_w, null, null, cd_estabelecimento_p, valor_bind_w);
										
			-- executa o comando sql com os respectivos binds

			valor_bind_w := sql_pck.executa_sql_cursor(	ds_select_w, valor_bind_w);
			
			loop
				fetch cursor_w bulk collect into	tb_seq_conta_w, tb_seq_conta_proc_w,
									tb_seq_conta_mat_w, tb_guia_referencia_w,
									tb_seq_segurado_w, tb_dt_item_w,
									tb_dt_item_hora_ini_w, tb_dt_item_hora_fim_w,
									tb_ie_origem_proced_w, tb_cd_procedimento_w,
									tb_nr_seq_material_w, tb_seq_selecao_w,
									tb_hr_inicial_w, tb_hr_final_w
				limit pls_util_pck.qt_registro_transacao_w;
				exit when tb_seq_conta_w.count = 0;
				
				nr_indice_w	:= 0;
				
				tb_seq_conta_ger_w.delete;
				tb_seq_conta_proc_ger_w.delete;
				tb_seq_conta_mat_ger_w.delete;
				tb_guia_referencia_ger_w.delete;
				tb_seq_segurado_ger_w.delete;
				tb_dt_item_ger_w.delete;
				tb_dt_item_hora_ini_ger_w.delete;
				tb_dt_item_hora_fim_ger_w.delete;
				tb_ie_origem_proced_ger_w.delete;
				tb_cd_procedimento_ger_w.delete;
				tb_nr_seq_material_ger_w.delete;
				tb_seq_selecao_ger_w.delete;
				
				for i in tb_seq_conta_w.first .. tb_seq_conta_w.last loop
					-- verifica se o registro a valido

					-- feito por performance para nao usar functions em clausulas where

					ie_registro_valido_w := pls_ocor_imp_pck.ie_reg_valido_fil_proc(	r_c_filtro.hr_inicial, tb_hr_inicial_w(i),
											r_c_filtro.hr_final, tb_hr_final_w(i));
					
					-- se for um registro valido grava na variavel que sera enviada para o banco como valida

					if (ie_registro_valido_w) then
						
						tb_seq_conta_ger_w(nr_indice_w)		:= tb_seq_conta_w(i);
						tb_seq_conta_proc_ger_w(nr_indice_w)	:= tb_seq_conta_proc_w(i);
						tb_seq_conta_mat_ger_w(nr_indice_w)	:= tb_seq_conta_mat_w(i);
						tb_guia_referencia_ger_w(nr_indice_w)	:= tb_guia_referencia_w(i);
						tb_seq_segurado_ger_w(nr_indice_w)	:= tb_seq_segurado_w(i);
						tb_dt_item_ger_w(nr_indice_w)		:= tb_dt_item_w(i);
						tb_dt_item_hora_ini_ger_w(nr_indice_w)	:= tb_dt_item_hora_ini_w(i);
						tb_dt_item_hora_fim_ger_w(nr_indice_w)	:= tb_dt_item_hora_fim_w(i);
						tb_ie_origem_proced_ger_w(nr_indice_w)	:= tb_ie_origem_proced_w(i);
						tb_cd_procedimento_ger_w(nr_indice_w)	:= tb_cd_procedimento_w(i);
						tb_nr_seq_material_ger_w(nr_indice_w)	:= tb_nr_seq_material_w(i);
						tb_seq_selecao_ger_w(nr_indice_w)	:= tb_seq_selecao_w(i);
					
						nr_indice_w	:= nr_indice_w + 1;
					end if;
				end loop;
				
				-- Insere todos os registros das listas na tabela de seleaao

				CALL CALL CALL CALL CALL pls_ocor_imp_pck.gerencia_selecao_filtro(	'F', ie_regra_excecao_p,
								nr_seq_filtro_p, nr_id_transacao_p,
								'S', tb_seq_conta_ger_w,
								tb_seq_conta_proc_ger_w, tb_seq_conta_mat_ger_w,
								tb_guia_referencia_ger_w, tb_seq_segurado_ger_w,
								tb_dt_item_ger_w, tb_dt_item_hora_ini_ger_w,
								tb_dt_item_hora_fim_ger_w, tb_ie_origem_proced_ger_w,
								tb_cd_procedimento_ger_w, tb_nr_seq_material_ger_w,
								tb_seq_selecao_ger_w);
			end loop;
			close cursor_w;
			
		exception
			when others then
			
			if (cursor_w%isopen) then
				close cursor_w;
			end if;
			-- Tratar erro gerado no sql dinamico, sera inserido registro no log e abortado o processo exibindo mensagem de erro.

			CALL CALL CALL CALL CALL CALL pls_ocor_imp_pck.trata_erro_sql_dinamico(	nr_seq_combinada_p, nr_seq_ocorrencia_p,
							ds_select_w, nr_id_transacao_p,
							nm_usuario_p, 'S');
		end;
		
		qt_filtro_processado_w := qt_filtro_processado_w + 1;
	end loop;
	
	-- se processou algum filtro

	if (qt_filtro_processado_w > 0) then
		-- Atualizar o campo definitivo que sera utilizado para sinalizar os registros que foram processados

		CALL CALL CALL CALL CALL pls_ocor_imp_pck.atualiza_campo_valido(	'F', ie_processo_excecao_p, ie_regra_excecao_p,
					ie_filtro_excecao_p, nr_id_transacao_p,
					nr_seq_filtro_p);
		ie_considera_selecao_p := true;
	end if;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_ocor_imp_pck.aplica_filtro_procedimento ( ie_considera_selecao_p INOUT boolean, ie_incidencia_selecao_regra_p text, ie_incidencia_selecao_filtro_p text, ie_processo_excecao_p text, ie_regra_excecao_p pls_oc_cta_combinada.ie_excecao%type, ie_filtro_excecao_p pls_oc_cta_filtro.ie_excecao%type, nr_seq_combinada_p pls_oc_cta_combinada.nr_sequencia%type, nr_seq_ocorrencia_p pls_ocorrencia.nr_sequencia%type, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type, nr_seq_filtro_p pls_oc_cta_filtro.nr_sequencia%type, nr_seq_lote_protocolo_p pls_protocolo_conta_imp.nr_seq_lote_protocolo%type, nr_seq_protocolo_p pls_protocolo_conta_imp.nr_sequencia%type, nr_seq_conta_p pls_conta_imp.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc_imp.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat_imp.nr_sequencia%type, dt_inicio_vigencia_p pls_oc_cta_combinada.dt_inicio_vigencia%type, dt_fim_vigencia_p pls_oc_cta_combinada.dt_fim_vigencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;
