CREATE OR REPLACE PROCEDURE pls_ocor_imp_pck.atualiza_campo_auxiliar ( ie_filtro_validacao_p text, ie_processo_excecao_p text, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type, nr_seq_filtro_p pls_oc_cta_filtro.nr_sequencia%type, ie_valido_temp_p text default 'N') AS $body$
BEGIN

-- Atualizar os campos _temp que sao responsaveis por auxiliar no controle do que deve ser validado


-- se estiver sendo processado um filtro

if (ie_filtro_validacao_p = 'F') then
	
	-- se for regra ou filtro de exceaao a necessario fazer uma preparaaao nos registros que serao selecionados

	if (ie_processo_excecao_p = 'S') then
	
		update	pls_oc_cta_selecao_imp
		set	ie_valido_temp = ie_valido_temp_p
		where	nr_id_transacao = nr_id_transacao_p
		and	ie_excecao = 'S';
		commit;
	else
		-- filtro bom em regra boa usa os campos ie_valido e ie_valido_temp

		update	pls_oc_cta_selecao_imp
		set	ie_valido_temp = ie_valido_temp_p
		where	nr_id_transacao = nr_id_transacao_p
		and	ie_valido = 'S'
		and	nr_seq_filtro = nr_seq_filtro_p;
		commit;
	end if;

-- se estiver sendo processada uma validaaao

elsif (ie_filtro_validacao_p = 'V') then
	
	update	pls_oc_cta_selecao_imp
	set	ie_valido_temp = ie_valido_temp_p
	where	nr_id_transacao = nr_id_transacao_p
	and	ie_valido = 'S';
	commit;
end if;

END;

$body$
