CREATE OR REPLACE PROCEDURE pls_ocor_imp_pck.atualiza_campo_valido ( ie_filtro_validacao_p text, ie_processo_excecao_p text, ie_regra_excecao_p pls_oc_cta_combinada.ie_excecao%type, ie_filtro_excecao_p pls_oc_cta_filtro.ie_excecao%type, nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type, nr_seq_filtro_p pls_oc_cta_filtro.nr_sequencia%type, ie_filtro_e_p text default 'N') AS $body$
DECLARE


ie_valido_w		varchar(1);
	

BEGIN
-- Atualizar os campos relacionados a informar que o registro a valido que sao responsaveis por auxiliar no controle do que

-- deve ser validado


-- se estiver sendo processado um filtro

if (ie_filtro_validacao_p = 'F') then
	
	-- nunca pode entrar aqui quando for exceaao de qualquer maneira

	if (ie_processo_excecao_p = 'N') then
	
		update	pls_oc_cta_selecao_imp
		set	ie_valido = 'N'
		where	nr_id_transacao = nr_id_transacao_p
		and	ie_valido_temp = 'N'
		and	nr_seq_filtro = nr_seq_filtro_p;
		commit;
			
	-- se for regra ou filtro de exceaao a necessario fazer uma preparaaao nos registros que serao selecionados

	elsif (ie_processo_excecao_p = 'S') then
			
		-- o altimo processo executado para as regras do tipo "E"

		if (ie_filtro_e_p = 'F') then
		
			-- filtro de exceaao com regra de exceaao valida o que estava invalido, por isso vira S

			if (ie_regra_excecao_p = 'S' and ie_filtro_excecao_p = 'S') then
				ie_valido_w := 'S';
			else
				ie_valido_w := 'N';
			end if;
			
			update	pls_oc_cta_selecao_imp
			set	ie_valido = ie_valido_w
			where	nr_id_transacao = nr_id_transacao_p
			and	ie_excecao = 'S';
			commit;
			
		else
			-- coloca ie_excecao = N para todos os registros que nao casaram com nenhum filtro

			update	pls_oc_cta_selecao_imp
			set	ie_excecao = 'N'
			where	nr_id_transacao = nr_id_transacao_p
			and	ie_excecao = 'S'
			and	ie_valido_temp = 'N';
			commit;
		end if;
	end if;

-- se estiver sendo processada uma validaaao

elsif (ie_filtro_validacao_p = 'V') then

	-- se for regra de exceaao, tudo o que foi selecionado faz o ie_valido ficar N

	if (ie_regra_excecao_p = 'S') then
		ie_valido_w := 'S';
	else
		-- se for regra boa, tudo o que nao foi selecionado faz o ie_valido ficar N

		ie_valido_w := 'N';
	end if;
	
	update	pls_oc_cta_selecao_imp
	set	ie_valido = 'N'
	where	nr_id_transacao = nr_id_transacao_p
	and	ie_valido_temp = ie_valido_w;
	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
