SET client_encoding TO 'UTF8';


-- Responsavel por atualizar o campo IE_LIBERADO que dira se o item a valido ou nao para a estrutura do tipo de limitaaao.



CREATE OR REPLACE PROCEDURE pls_ocor_imp_pck.atualizar_registro ( ie_proc_mat_p text, dados_tabela_aux_val_limit_p pls_ocor_imp_pck.dados_tabela_aux_val_limit, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

-- Deve ser verificado se a por procedimento ou por material para que se tenha certeza de que esta sendo ataulizada a linha correta

-- e seja acessado a tabela pelos campos corretos e nao fique sendo verificado nas restriaaes do update pois tende a ser mais custoso.

-- Procedimento

if (ie_proc_mat_p = 'P') then

	-- Atualiza o campo IE_LIBERADO conforme o cadastrado para o procedimento do tipo de limitaaao informado.

	update	pls_oc_cta_val_limit_aux
	set	ie_liberado		= dados_tabela_aux_val_limit_p.ie_liberado,
		dt_atualizacao_nrec	= dt_atualizacao,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario_nrec		= nm_usuario,
		nm_usuario		= nm_usuario_p
	where	nr_id_transacao		= dados_tabela_aux_val_limit_p.nr_id_transacao
	and	nr_seq_tipo_limitacao	= dados_tabela_aux_val_limit_p.nr_seq_tipo_limitacao
	and	nr_seq_segurado		= dados_tabela_aux_val_limit_p.nr_seq_segurado
	and	nr_seq_conta		= dados_tabela_aux_val_limit_p.nr_seq_conta
	and	nr_seq_conta_proc	= dados_tabela_aux_val_limit_p.nr_seq_conta_proc;
-- Material

elsif (ie_proc_mat_p = 'M') then

	-- Atualiza o campo IE_LIBERADO conforme o cadastrado para o material do tipo de limitaaao informado.

	update	pls_oc_cta_val_limit_aux
	set	ie_liberado		= dados_tabela_aux_val_limit_p.ie_liberado,
		dt_atualizacao_nrec	= dt_atualizacao,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario_nrec		= nm_usuario,
		nm_usuario		= nm_usuario_p
	where	nr_id_transacao		= dados_tabela_aux_val_limit_p.nr_id_transacao
	and	nr_seq_tipo_limitacao	= dados_tabela_aux_val_limit_p.nr_seq_tipo_limitacao
	and	nr_seq_segurado		= dados_tabela_aux_val_limit_p.nr_seq_segurado
	and	nr_seq_conta		= dados_tabela_aux_val_limit_p.nr_seq_conta
	and	nr_seq_conta_mat	= dados_tabela_aux_val_limit_p.nr_seq_conta_mat;
end if;
commit;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
