CREATE OR REPLACE PROCEDURE pls_ocor_imp_pck.finaliza_processo_filtro (nr_id_transacao_p pls_oc_cta_selecao_imp.nr_id_transacao%type) AS $body$
BEGIN

-- Faz a finalizaaao do processamento dos filtros. Consiste basicamente em marcar as

-- contas que tiveram itens excluados por processos de exceaao e limpar todos os invalidos.

insert into  pls_oc_cta_conta_ex_imp(nr_seq_conta)
SELECT	distinct nr_seq_conta
from	pls_oc_cta_selecao_imp
where	nr_id_transacao = nr_id_transacao_p
and	ie_valido = 'N'
and	ie_marcado_excecao = 'S';
commit;

-- limpa tudo o que for invalido, porque na tabela de seleaao sa podera ser utilizado o que for ie_valido = S nos passos seguintes

CALL CALL CALL CALL CALL CALL pls_ocor_imp_pck.limpar_invalidos(nr_id_transacao_p);

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
