CREATE OR REPLACE PROCEDURE pls_ocor_imp_pck.inserir_ocorrencia_imp ( nr_seq_ocorrencia_p pls_ocorrencia.nr_sequencia%type, tb_nr_seq_conta_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_conta_proc_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_conta_mat_p INOUT pls_util_cta_pck.t_number_table, tb_ds_observacao_p INOUT pls_util_cta_pck.t_varchar2_table_4000, nr_indice_p INOUT integer, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN
-- se tiver registros manda para o banco

if (tb_nr_seq_conta_p.count > 0) then
	forall i in tb_nr_seq_conta_p.first .. tb_nr_seq_conta_p.last
		insert 	into pls_ocorrencia_imp(
			nr_sequencia, nm_usuario_nrec, dt_atualizacao_nrec,
			nm_usuario, dt_atualizacao, nr_seq_ocorrencia,
			nr_seq_conta, nr_seq_conta_proc, nr_seq_conta_mat,
			ds_observacao, ie_situacao, ie_incidencia_ocor
		) values (
			nextval('pls_ocorrencia_imp_seq'), nm_usuario_p, clock_timestamp(),
			nm_usuario_p, clock_timestamp(), nr_seq_ocorrencia_p,
			tb_nr_seq_conta_p(i), tb_nr_seq_conta_proc_p(i), tb_nr_seq_conta_mat_p(i),
			tb_ds_observacao_p(i), 'A', pls_ocor_imp_pck.obter_tipo_registro(tb_nr_seq_conta_p(i),
									tb_nr_seq_conta_proc_p(i), 
									tb_nr_seq_conta_mat_p(i))
		);
	commit;
	
	for i in tb_nr_seq_conta_p.first .. tb_nr_seq_conta_p.last loop
		CALL pls_gerar_ocor_cta_hist(tb_nr_seq_conta_p(i), nr_seq_ocorrencia_p, nm_usuario_p, 'I');
	end loop;
end if;

tb_nr_seq_conta_p.delete;
tb_nr_seq_conta_proc_p.delete;
tb_nr_seq_conta_mat_p.delete;
tb_ds_observacao_p.delete;
nr_indice_p := 0;

END;

$body$
