SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_ocor_imp_pck.limpar_ocorrencias ( nr_seq_lote_protocolo_p pls_protocolo_conta_imp.nr_seq_lote_protocolo%type, nr_seq_protocolo_p pls_protocolo_conta_imp.nr_sequencia%type, nr_seq_conta_p pls_conta_imp.nr_sequencia%type) AS $body$
BEGIN

-- A ordem sera do mais especifico para o mais amplo

-- comeaa pela conta

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then

	delete	FROM pls_ocorrencia_imp	a
	where	a.nr_seq_conta	= nr_seq_conta_p;

-- Protocolo

elsif (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then

	delete	FROM pls_ocorrencia_imp	a
	where	a.nr_seq_conta	in (	SELECT	x.nr_sequencia
					from	pls_conta_imp		x
					where	x.nr_seq_protocolo	= nr_seq_protocolo_p);

-- Lote

elsif (nr_seq_lote_protocolo_p IS NOT NULL AND nr_seq_lote_protocolo_p::text <> '') then

	
	delete	FROM pls_ocorrencia_imp	a
	where	a.nr_seq_conta	in (	SELECT	x.nr_sequencia
					from	pls_conta_imp		x,
						pls_protocolo_conta_imp	y
					where	y.nr_sequencia		= x.nr_seq_protocolo
					and	y.nr_seq_lote_protocolo	= nr_seq_lote_protocolo_p);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_ocor_imp_pck.limpar_ocorrencias ( nr_seq_lote_protocolo_p pls_protocolo_conta_imp.nr_seq_lote_protocolo%type, nr_seq_protocolo_p pls_protocolo_conta_imp.nr_sequencia%type, nr_seq_conta_p pls_conta_imp.nr_sequencia%type) FROM PUBLIC;
