SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_ocor_imp_pck.obter_restricao_contrato ( nr_contrato_p pls_oc_cta_filtro_contrato.nr_contrato%type, nr_seq_grupo_contrato_p pls_oc_cta_filtro_contrato.nr_seq_grupo_contrato%type, ds_tabelas_p INOUT text, valor_bind_p INOUT sql_pck.t_dado_bind) AS $body$
DECLARE

					
ds_alias_conta_w	varchar(5);
ds_alias_seg_w		varchar(5);
ds_alias_contr_w	varchar(5);

ds_restricao_w		varchar(1000);
ds_tabelas_w		varchar(1000);
ie_valor_filtro_w	boolean := false;


BEGIN
-- Monta as restriaaes e binds dos campos de filtro de contrato

ds_restricao_w := null;
ds_tabelas_w := null;

-- obtam o alias da tabela 

ds_alias_conta_w := pls_ocor_imp_pck.obter_alias_tabela('pls_conta_imp');
ds_alias_seg_w := 'seg';
ds_alias_contr_w := 'contr';

--Monta Ligaaao das tabelas PLS_SEGURADO e PLS_CONTRATO

ds_tabelas_w	:= ds_tabelas_w || ', ' || pls_util_pck.enter_w ||
			'	pls_segurado ' || ds_alias_seg_w || ', ' || pls_util_pck.enter_w ||
			'	pls_contrato ' || ds_alias_contr_w;
			
ds_restricao_w	:= ds_restricao_w || 'and ' || ds_alias_seg_w || '.nr_sequencia = ' || ds_alias_conta_w || '.nr_seq_segurado_conv ' || pls_util_pck.enter_w ||
			'and	' || ds_alias_contr_w || '.nr_sequencia = ' || ds_alias_seg_w || '.nr_seq_contrato ' || pls_util_pck.enter_w; 			
			
-- Contrato

if (nr_contrato_p IS NOT NULL AND nr_contrato_p::text <> '') then
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w ||
				'and	' || ds_alias_contr_w || '.nr_contrato = :nr_contrato_pc ';

	valor_bind_p := sql_pck.bind_variable(	':nr_contrato_pc', nr_contrato_p, valor_bind_p);
	ie_valor_filtro_w := true;
end if;

-- Grupo de contrato

if (nr_seq_grupo_contrato_p IS NOT NULL AND nr_seq_grupo_contrato_p::text <> '') then
	
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w ||
				'and	exists	(select	1' || pls_util_pck.enter_w ||
				'		from	pls_preco_contrato x ' || pls_util_pck.enter_w ||
				'		where	x.nr_seq_grupo = :nr_seq_grupo_contrato_pc ' || pls_util_pck.enter_w ||
				'		and	x.nr_seq_contrato = ' || ds_alias_contr_w || '.nr_sequencia)';
	
	valor_bind_p := sql_pck.bind_variable(	':nr_seq_grupo_contrato_pc', nr_seq_grupo_contrato_p, valor_bind_p);
	ie_valor_filtro_w := true;
end if;

--Caso seja cadastrado um filtro para contrato em branco nao precisa relacionar as tabelas pls_segurado e pls_contrato

if (ie_valor_filtro_w = false) then
	ds_restricao_w := null;
	ds_tabelas_w   := null;
end if;

ds_tabelas_p	:= ds_tabelas_w;

return;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
