CREATE OR REPLACE FUNCTION pls_ocor_imp_pck.obter_restricao_material ( nr_seq_material_p pls_oc_cta_filtro_mat.nr_seq_material%type, nr_seq_grupo_material_p pls_oc_cta_filtro_mat.nr_seq_grupo_material%type, nr_seq_estrut_mat_p pls_oc_cta_filtro_mat.nr_seq_estrut_mat%type, ie_tipo_despesa_mat_p pls_oc_cta_filtro_mat.ie_tipo_despesa_mat%type, dt_dia_semana_p pls_oc_cta_filtro_mat.dt_dia_semana%type, ie_feriado_p pls_oc_cta_filtro_mat.ie_feriado%type, ie_tipo_feriado_p pls_oc_cta_filtro_mat.ie_tipo_feriado%type, hr_inicial_p pls_oc_cta_filtro_mat.hr_inicial%type, hr_final_p pls_oc_cta_filtro_mat.hr_final%type, vl_minimo_item_p pls_oc_cta_filtro_mat.vl_minimo_item%type, vl_max_item_p pls_oc_cta_filtro_mat.vl_max_item%type, ie_material_tuss_p pls_oc_cta_filtro_mat.ie_material_tuss%type, ds_campos_p INOUT text, ds_campos_proc_p INOUT text, valor_bind_p INOUT sql_pck.t_dado_bind) AS $body$
DECLARE

					
ds_alias_conta_w	varchar(5);
ds_alias_mat_w		varchar(5);
		
ds_restricao_w		varchar(5000);
ds_campos_w		varchar(1000);
ds_campos_proc_w	varchar(1000);


BEGIN
-- Monta as restriaaes e binds dos campos de filtro de procedimento

ds_restricao_w := null;

-- obtam o alias da tabela 

ds_alias_conta_w := pls_ocor_imp_pck.obter_alias_tabela('pls_conta_imp');
ds_alias_mat_w   := pls_ocor_imp_pck.obter_alias_tabela('pls_conta_mat_imp');

--Caso a incidancia seja PM precisa criar o campo para o union do procedimento nulo

ds_campos_proc_w := ',' || pls_util_pck.enter_w || ' null hr_inicial ,' || pls_util_pck.enter_w ||
			' null hr_final ';
			
-- Sequancia do material

if (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w ||
				'and	' || ds_alias_mat_w || '.nr_seq_material_conv = :nr_seq_material_pc ';

	valor_bind_p := sql_pck.bind_variable(	':nr_seq_material_pc', nr_seq_material_p, valor_bind_p);
end if;

-- Grupo do material

if (nr_seq_grupo_material_p IS NOT NULL AND nr_seq_grupo_material_p::text <> '') then
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w ||
						'and	exists	(select	1 ' || pls_util_pck.enter_w ||
						'		from	pls_preco_grupo_mat_v xy ' || pls_util_pck.enter_w ||
						'		where	xy.nr_seq_material = ' || ds_alias_mat_w || '.nr_seq_material_conv ' || pls_util_pck.enter_w ||
						'		and	xy.nr_seq_grupo = :nr_seq_grupo_material_pc) ';
	
	valor_bind_p := sql_pck.bind_variable(	':nr_seq_grupo_material_pc', nr_seq_grupo_material_p, valor_bind_p);
end if;

-- Estrutura de material

if (nr_seq_estrut_mat_p IS NOT NULL AND nr_seq_estrut_mat_p::text <> '') then
	-- gerencia a atualizaaao da tabela TM

	CALL pls_gerencia_upd_obj_pck.atualizar_objetos('tasy', 'PLS_OCOR_IMP_PCK.OBTER_RESTRICAO_MATERIAL()', 'PLS_ESTRUTURA_MATERIAL_TM');

	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w ||
						'and	exists	(select	1 '  || pls_util_pck.enter_w ||
						'		from	pls_estrutura_material_tm  estrut_mat ' || pls_util_pck.enter_w ||
						'		where	estrut_mat.nr_seq_estrutura = :nr_seq_estrut_mat_pc ' || pls_util_pck.enter_w ||
						'		and	estrut_mat.nr_seq_material = ' || ds_alias_mat_w || '.nr_seq_material_conv) ';
	
	valor_bind_p := sql_pck.bind_variable(	':nr_seq_estrut_mat_pc', nr_seq_estrut_mat_p, valor_bind_p);
end if;

-- Tipo de despesa

if (ie_tipo_despesa_mat_p IS NOT NULL AND ie_tipo_despesa_mat_p::text <> '') then
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w ||
				'and	' || ds_alias_mat_w || '.ie_tipo_despesa_conv = :ie_tipo_despesa_pc ';
	
	valor_bind_p := sql_pck.bind_variable(	':ie_tipo_despesa_pc', ie_tipo_despesa_mat_p, valor_bind_p);
end if;

-- Dia semana

if (dt_dia_semana_p IS NOT NULL AND dt_dia_semana_p::text <> '') then
	
	if (dt_dia_semana_p <> 9) then
		ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w ||
					'and	' || ds_alias_mat_w || '.dt_dia_semana_exec_conv = :dt_dia_semana_pc ';
				
		valor_bind_p := sql_pck.bind_variable(	':dt_dia_semana_pc', dt_dia_semana_p, valor_bind_p);		
	else
		ds_restricao_w := 	ds_restricao_w || pls_util_pck.enter_w ||
					'and	' || ds_alias_mat_w || '.dt_dia_semana_exec_conv in (2,3,4,5,6) ';
	end if;	
				
end if;

-- Feriado

if (ie_feriado_p = 'S') then
		ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w ||
						'and	exists	(select	1' || pls_util_pck.enter_w ||
						'		from	feriado xy' || pls_util_pck.enter_w ||
						'		where	xy.cd_estabelecimento = ' || ds_alias_conta_w || '.cd_estabelecimento ' || pls_util_pck.enter_w ||
						'		and	xy.dt_feriado = ' || ds_alias_mat_w || '.dt_execucao_trunc_conv) ';
end if;

-- Tipo feriado

if (ie_tipo_feriado_p IS NOT NULL AND ie_tipo_feriado_p::text <> '') then
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w ||
						'and	exists	(select	1' || pls_util_pck.enter_w ||
						'		from	feriado xz' || pls_util_pck.enter_w ||
						'		where	xz.cd_estabelecimento = ' || ds_alias_conta_w || '.cd_estabelecimento ' || pls_util_pck.enter_w ||
						'		and	xz.dt_feriado = ' || ds_alias_mat_w || '.dt_execucao_trunc_conv' || pls_util_pck.enter_w ||
						'		and	xz.ie_tipo_feriado = :ie_tipo_feriado_pc) ';
						
	valor_bind_p := sql_pck.bind_variable(	':ie_tipo_feriado_pc', ie_tipo_feriado_p, valor_bind_p);
end if;

-- Hora inicial

if (hr_inicial_p IS NOT NULL AND hr_inicial_p::text <> '') then
	ds_campos_w :=	ds_campos_w || ',' || pls_util_pck.enter_w ||
						' pls_manipulacao_datas_pck.obter_hora_data_atual( ' || ds_alias_mat_w || '.dt_inicio) hr_inicial ';
else
	ds_campos_w :=	ds_campos_w || ',' || pls_util_pck.enter_w ||
						' null hr_inicial ';
end if;

-- Hora final

if (hr_final_p IS NOT NULL AND hr_final_p::text <> '') then
	ds_campos_w :=	ds_campos_w || ',' || pls_util_pck.enter_w ||
						' pls_manipulacao_datas_pck.obter_hora_data_atual( ' || ds_alias_mat_w || '.dt_fim) hr_final ';
else
	ds_campos_w :=	ds_campos_w || ',' || pls_util_pck.enter_w ||
						' null hr_final ';
end if;

-- Valor manimo

if (vl_minimo_item_p IS NOT NULL AND vl_minimo_item_p::text <> '') then
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w ||
				'and	' || ds_alias_mat_w || '.vl_total <= :vl_minimo_pc ';
	
	valor_bind_p := sql_pck.bind_variable(	':vl_minimo_pc', vl_minimo_item_p, valor_bind_p);
end if;

-- Valor maximo

if (vl_max_item_p IS NOT NULL AND vl_max_item_p::text <> '') then
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w ||
				'and	' || ds_alias_mat_w || '.vl_total >= :vl_maximo_pc ';
	
	valor_bind_p := sql_pck.bind_variable(	':vl_maximo_pc', vl_max_item_p, valor_bind_p);
end if;

if (ie_material_tuss_p = 'PC') then
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w ||
				' and	exists(	select	1 ' || pls_util_pck.enter_w ||
				'		from	pls_material x ' || pls_util_pck.enter_w ||
				'		where	x.nr_sequencia = ' || ds_alias_mat_w || '.nr_seq_material_conv ' || pls_util_pck.enter_w ||
				'		and	x.nr_seq_tuss_mat_item is not null) ';
elsif (ie_material_tuss_p = 'NP') then
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w ||
				' and	exists(	select	1 ' || pls_util_pck.enter_w ||
				'		from	pls_material x ' || pls_util_pck.enter_w ||
				'		where	x.nr_sequencia = ' || ds_alias_mat_w || '.nr_seq_material_conv ' || pls_util_pck.enter_w ||
				'		and	x.nr_seq_tuss_mat_item is null) ';
end if;

ds_campos_p 	 := ds_campos_w;
ds_campos_proc_p := ds_campos_proc_w;

return;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION pls_ocor_imp_pck.obter_restricao_material ( nr_seq_material_p pls_oc_cta_filtro_mat.nr_seq_material%type, nr_seq_grupo_material_p pls_oc_cta_filtro_mat.nr_seq_grupo_material%type, nr_seq_estrut_mat_p pls_oc_cta_filtro_mat.nr_seq_estrut_mat%type, ie_tipo_despesa_mat_p pls_oc_cta_filtro_mat.ie_tipo_despesa_mat%type, dt_dia_semana_p pls_oc_cta_filtro_mat.dt_dia_semana%type, ie_feriado_p pls_oc_cta_filtro_mat.ie_feriado%type, ie_tipo_feriado_p pls_oc_cta_filtro_mat.ie_tipo_feriado%type, hr_inicial_p pls_oc_cta_filtro_mat.hr_inicial%type, hr_final_p pls_oc_cta_filtro_mat.hr_final%type, vl_minimo_item_p pls_oc_cta_filtro_mat.vl_minimo_item%type, vl_max_item_p pls_oc_cta_filtro_mat.vl_max_item%type, ie_material_tuss_p pls_oc_cta_filtro_mat.ie_material_tuss%type, ds_campos_p INOUT text, ds_campos_proc_p INOUT text, valor_bind_p INOUT sql_pck.t_dado_bind) FROM PUBLIC;
