SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_ocor_imp_pck.obter_restricao_operadora ( nr_seq_congenere_p pls_oc_cta_filtro_oper.nr_seq_congenere%type, nr_seq_congenere_sup_p pls_oc_cta_filtro_oper.nr_seq_congenere_sup%type, nr_seq_grupo_cooperativa_p pls_oc_cta_filtro_oper.nr_seq_grupo_cooperativa%type, sg_estado_operadora_p pls_oc_cta_filtro_oper.sg_estado_operadora%type, ie_tipo_intercambio_p pls_oc_cta_filtro_oper.ie_tipo_intercambio%type, valor_bind_p INOUT sql_pck.t_dado_bind, ds_tabelas_p INOUT text) AS $body$
DECLARE

ds_alias_conta_w	varchar(5);
ds_alias_seg_w		varchar(5);

ds_restricao_w		varchar(2000);
ds_tabelas_w		varchar(1000);
ie_valor_filtro_w	boolean := false;


BEGIN
-- Monta as restriaaes e binds dos campos de filtro de beneficiario

ds_restricao_w := null;
ds_tabelas_w := null;

-- obtam o alias da tabela 

ds_alias_conta_w := pls_ocor_imp_pck.obter_alias_tabela('pls_conta_imp');
ds_alias_seg_w := 'seg';

--Monta Ligaaao da tabela PLS_SEGURADO

ds_tabelas_w	:= ds_tabelas_w || ', ' || pls_util_pck.enter_w ||
			'	pls_segurado ' || ds_alias_seg_w;

ds_restricao_w	:= ds_restricao_w || 'and ' || ds_alias_seg_w || '.nr_sequencia = ' || ds_alias_conta_w || '.nr_seq_segurado_conv '; 			

--Congenere

if (nr_seq_congenere_p IS NOT NULL AND nr_seq_congenere_p::text <> '') then
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w||
					'and	' || ds_alias_seg_w || '.nr_seq_congenere_ok = :nr_seq_congenere_pc';
					
	valor_bind_p := sql_pck.bind_variable(	':nr_seq_congenere_pc', nr_seq_congenere_p, valor_bind_p);
	ie_valor_filtro_w := true;
end if;

--Congenere sup

if (nr_seq_congenere_sup_p IS NOT NULL AND nr_seq_congenere_sup_p::text <> '') then
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w ||
				'and	exists(select 1 ' || pls_util_pck.enter_w ||
				'	       from   pls_congenere w' ||  pls_util_pck.enter_w ||
				'	       where  w.nr_sequencia = ' || ds_alias_seg_w || '.nr_seq_congenere_ok ' || pls_util_pck.enter_w ||
				'	       and    w.nr_seq_congenere = :nr_seq_congenere_sup_pc)';
	valor_bind_p := sql_pck.bind_variable(	':nr_seq_congenere_sup_pc', nr_seq_congenere_sup_p, valor_bind_p);
	ie_valor_filtro_w := true;
end if;

--Grupo cooperativa

if (nr_seq_grupo_cooperativa_p IS NOT NULL AND nr_seq_grupo_cooperativa_p::text <> '') then
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w||
					'and	' || ds_alias_seg_w || '.nr_seq_grupo_coop = :nr_seq_grupo_coop_pc';
					
	valor_bind_p := sql_pck.bind_variable(	':nr_seq_grupo_coop_pc', nr_seq_grupo_cooperativa_p, valor_bind_p);
	ie_valor_filtro_w := true;
end if;

--Estado operadora

if (sg_estado_operadora_p IS NOT NULL AND sg_estado_operadora_p::text <> '') then
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w||
					'and	' || ds_alias_conta_w || '.sg_estado_operadora_conv = :sg_estado_operadora_pc';
					
	valor_bind_p := sql_pck.bind_variable(	':sg_estado_operadora_pc', sg_estado_operadora_p, valor_bind_p);
	ie_valor_filtro_w := true;
end if;

-- Tipo de intercambio (Nacional ou Estadual) Sa  adiciona a restriaao se ele for diferente de A (Ambos), caso ele seja igual a A ou nulo entao nao deve ser adicionado a restriaao

if (ie_tipo_intercambio_p <> 'A') then
	ds_restricao_w :=	ds_restricao_w || pls_util_pck.enter_w||
					'and	' || ds_alias_conta_w || '.ie_tipo_inter_oper_conv = :ie_tipo_intercambio_pc';
					
	valor_bind_p := sql_pck.bind_variable(	':ie_tipo_intercambio_pc', ie_tipo_intercambio_p, valor_bind_p);
	ie_valor_filtro_w := true;
end if;

--Caso seja cadastrado um filtro para operadora em branco nao precisa relacionar a tabela pls_segurado

if (ie_valor_filtro_w = false) then
	ds_restricao_w := null;
	ds_tabelas_w   := null;
end if;
	
ds_tabelas_p	:= ds_tabelas_w;

return;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION pls_ocor_imp_pck.obter_restricao_operadora ( nr_seq_congenere_p pls_oc_cta_filtro_oper.nr_seq_congenere%type, nr_seq_congenere_sup_p pls_oc_cta_filtro_oper.nr_seq_congenere_sup%type, nr_seq_grupo_cooperativa_p pls_oc_cta_filtro_oper.nr_seq_grupo_cooperativa%type, sg_estado_operadora_p pls_oc_cta_filtro_oper.sg_estado_operadora%type, ie_tipo_intercambio_p pls_oc_cta_filtro_oper.ie_tipo_intercambio%type, valor_bind_p INOUT sql_pck.t_dado_bind, ds_tabelas_p INOUT text) FROM PUBLIC;
