CREATE OR REPLACE FUNCTION pls_ocor_imp_pck.obter_tipo_registro ( nr_seq_conta_p pls_oc_cta_selecao_imp.nr_seq_conta%type, nr_seq_conta_proc_p pls_oc_cta_selecao_imp.nr_seq_conta_proc%type, nr_seq_conta_mat_p pls_oc_cta_selecao_imp.nr_seq_conta_mat%type) RETURNS varchar AS $body$
DECLARE


ie_tipo_registro_w	varchar(1);


BEGIN

-- se sa tem a conta informada a incidencia a de conta

if ((nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') and
	 coalesce(nr_seq_conta_proc_p::text, '') = '' and 
	 coalesce(nr_seq_conta_mat_p::text, '') = '') then
	ie_tipo_registro_w := 'C';
else
	if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
		-- procedimento

		ie_tipo_registro_w := 'P';
	else
		-- material

		ie_tipo_registro_w := 'M';
	end if;
end if;

return ie_tipo_registro_w;

END;

