CREATE OR REPLACE PROCEDURE pls_ocor_imp_pck.pls_insert_agrup_ocor (tb_nr_seq_ocor_imp_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_agrupador_p INOUT pls_util_cta_pck.t_number_table, nr_contador_p INOUT integer, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN
if (tb_nr_seq_ocor_imp_p.count > 0) then
	forall i in tb_nr_seq_ocor_imp_p.first..tb_nr_seq_ocor_imp_p.last
		update	pls_ocorrencia_imp
		set	ie_agrupador_obs	= tb_nr_seq_agrupador_p(i),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= tb_nr_seq_ocor_imp_p(i);

	commit;
end if;	

-- Apas realizar o update, limpar as variaveis table e zerar o contador

tb_nr_seq_ocor_imp_p.delete;
tb_nr_seq_agrupador_p.delete;
nr_contador_p	:= 0;

END;

