CREATE OR REPLACE PROCEDURE pls_odonto_pck.validar_limitacao_proc ( nr_seq_segurado_p bigint, nr_seq_guia_proc_p bigint, nr_seq_conta_proc_p bigint, nr_seq_reembolso_proc_p bigint, nr_seq_requisicao_proc_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, qt_solicitada_p bigint, nr_seq_guia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_local_p text, nr_seq_dente_p text, ie_face_distal_p text, ie_face_incisal_p text, ie_face_lingual_p text, ie_face_mesial_p text, ie_face_oclusal_p text, ie_face_palatina_p text, ie_face_vestibular_p text) AS $body$
