c01 CURSOR FOR
	SELECT	/*+ CHOOSE*/		a.nr_sequencia,
		a.ie_limitacao ie_limita,
		a.nr_seq_tipo_limitacao,
		c.qt_permitida,
		c.qt_meses_intervalo,
		c.ie_periodo,
		c.nr_seq_contrato,
		c.nr_seq_plano,
		c.ie_tipo_periodo,
		c.nr_sequencia,
		a.nr_seq_grupo_servico,
		a.cd_area_procedimento,
		a.cd_especialidade,
		a.cd_grupo_proc,
		a.cd_procedimento,
		CASE WHEN ie_limitacao='S' THEN  0  ELSE 1 END  limita,
		c.ie_qt_familia
	from	pls_tipo_limitacao b,
		pls_limitacao_proc a,
		pls_limitacao c
	where	a.nr_seq_tipo_limitacao	= b.nr_sequencia
	and	c.nr_seq_tipo_limitacao	= b.nr_sequencia
	and	((coalesce(a.cd_procedimento::text, '') = '') or (a.cd_procedimento = cd_procedimento_p))
	and	((coalesce(a.ie_origem_proced::text, '') = '') or (a.ie_origem_proced = ie_origem_proced_w))
	and	((coalesce(a.cd_grupo_proc::text, '') = '') or (a.cd_grupo_proc = cd_grupo_w))
	and	((coalesce(a.cd_especialidade::text, '') = '') or (a.cd_especialidade = cd_especialidade_w))
	and	((coalesce(a.cd_area_procedimento::text, '') = '') or (a.cd_area_procedimento = cd_area_w))
	and	coalesce(a.cd_doenca_cid::text, '') = ''
	and	coalesce(a.ie_tipo_guia::text, '') = ''
	and	b.ie_situacao	= 'A'
	and	c.nr_seq_contrato	= nr_seq_contrato_w
	and	((c.NR_SEQ_PLANO_CONTRATO	= nr_seq_plano_w) or (coalesce(c.NR_SEQ_PLANO_CONTRATO::text, '') = ''))
	and	ie_forma_limitacao_w = 'C'
	and	clock_timestamp() between coalesce(c.dt_inicio_vigencia,clock_timestamp()) and coalesce(c.dt_fim_vigencia,clock_timestamp())
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.nr_seq_local = nr_seq_local_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.nr_seq_dente = nr_seq_dente_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_distal = ie_face_distal_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_incisal = ie_face_incisal_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_lingual = ie_face_lingual_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_mesial = ie_face_mesial_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_oclusal = ie_face_oclusal_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_palatina = ie_face_palatina_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_vestibular = ie_face_vestibular_p))
	
union all

	SELECT	/*+ CHOOSE*/		a.nr_sequencia,
		a.ie_limitacao ie_limita,
		a.nr_seq_tipo_limitacao,
		c.qt_permitida,
		c.qt_meses_intervalo,
		c.ie_periodo,
		c.nr_seq_contrato,
		c.nr_seq_plano,
		c.ie_tipo_periodo,
		c.nr_sequencia,
		a.nr_seq_grupo_servico,
		a.cd_area_procedimento,
		a.cd_especialidade,
		a.cd_grupo_proc,
		a.cd_procedimento,
		CASE WHEN ie_limitacao='S' THEN  0  ELSE 1 END  limita,
		c.ie_qt_familia
	from	pls_tipo_limitacao b,
		pls_limitacao_proc a,
		pls_limitacao c
	where	a.nr_seq_tipo_limitacao	= b.nr_sequencia
	and	c.nr_seq_tipo_limitacao	= b.nr_sequencia
	and	((coalesce(a.cd_procedimento::text, '') = '') or (a.cd_procedimento = cd_procedimento_p))
	and	((coalesce(a.ie_origem_proced::text, '') = '') or (a.ie_origem_proced = ie_origem_proced_w))
	and	((coalesce(a.cd_grupo_proc::text, '') = '') or (a.cd_grupo_proc = cd_grupo_w))
	and	((coalesce(a.cd_especialidade::text, '') = '') or (a.cd_especialidade = cd_especialidade_w))
	and	((coalesce(a.cd_area_procedimento::text, '') = '') or (a.cd_area_procedimento = cd_area_w))
	and	coalesce(a.cd_doenca_cid::text, '') = ''
	and	coalesce(a.ie_tipo_guia::text, '') = ''
	and	b.ie_situacao	= 'A'	
	and	ie_forma_limitacao_w = 'I'
	and	clock_timestamp() between coalesce(c.dt_inicio_vigencia,clock_timestamp()) and coalesce(c.dt_fim_vigencia,clock_timestamp())
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.nr_seq_local = nr_seq_local_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.nr_seq_dente = nr_seq_dente_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_distal = ie_face_distal_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_incisal = ie_face_incisal_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_lingual = ie_face_lingual_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_mesial = ie_face_mesial_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_oclusal = ie_face_oclusal_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_palatina = ie_face_palatina_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_vestibular = ie_face_vestibular_p))
	
union all

	select	/*+ CHOOSE*/		a.nr_sequencia,
		a.ie_limitacao ie_limita,
		a.nr_seq_tipo_limitacao,
		c.qt_permitida,
		c.qt_meses_intervalo,
		c.ie_periodo,
		c.nr_seq_contrato,
		c.nr_seq_plano,
		c.ie_tipo_periodo,
		c.nr_sequencia,
		a.nr_seq_grupo_servico,
		a.cd_area_procedimento,
		a.cd_especialidade,
		a.cd_grupo_proc,
		a.cd_procedimento,
		CASE WHEN ie_limitacao='S' THEN  0  ELSE 1 END  limita,
		c.ie_qt_familia
	from	pls_tipo_limitacao b,
		pls_limitacao_proc a,
		pls_limitacao c
	where	a.nr_seq_tipo_limitacao	= b.nr_sequencia
	and	c.nr_seq_tipo_limitacao	= b.nr_sequencia
	and	((coalesce(a.cd_procedimento::text, '') = '') or (a.cd_procedimento = cd_procedimento_p))
	and	((coalesce(a.ie_origem_proced::text, '') = '') or (a.ie_origem_proced = ie_origem_proced_w))
	and	((coalesce(a.cd_grupo_proc::text, '') = '') or (a.cd_grupo_proc = cd_grupo_w))
	and	((coalesce(a.cd_especialidade::text, '') = '') or (a.cd_especialidade = cd_especialidade_w))
	and	((coalesce(a.cd_area_procedimento::text, '') = '') or (a.cd_area_procedimento = cd_area_w))
	and	coalesce(a.cd_doenca_cid::text, '') = ''
	and	coalesce(a.ie_tipo_guia::text, '') = ''
	and	b.ie_situacao	= 'A'
	and	c.nr_seq_plano	= nr_seq_plano_w
	and	ie_forma_limitacao_w = 'P'
	and	clock_timestamp() between coalesce(c.dt_inicio_vigencia,clock_timestamp()) and coalesce(c.dt_fim_vigencia,clock_timestamp())
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.nr_seq_local = nr_seq_local_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.nr_seq_dente = nr_seq_dente_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_distal = ie_face_distal_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_incisal = ie_face_incisal_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_lingual = ie_face_lingual_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_mesial = ie_face_mesial_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_oclusal = ie_face_oclusal_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_palatina = ie_face_palatina_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_vestibular = ie_face_vestibular_p))
	
union all

	select	/*+ CHOOSE*/		a.nr_sequencia,
		a.ie_limitacao ie_limita,
		a.nr_seq_tipo_limitacao,
		c.qt_permitida,
		c.qt_meses_intervalo,
		c.ie_periodo,
		c.nr_seq_contrato,
		c.nr_seq_plano,
		c.ie_tipo_periodo,
		c.nr_sequencia,
		a.nr_seq_grupo_servico,
		a.cd_area_procedimento,
		a.cd_especialidade,
		a.cd_grupo_proc,
		a.cd_procedimento,
		CASE WHEN ie_limitacao='S' THEN  0  ELSE 1 END  limita,
		c.ie_qt_familia
	from	pls_tipo_limitacao b,
		pls_limitacao_proc a,
		pls_limitacao c
	where	a.nr_seq_tipo_limitacao	= b.nr_sequencia
	and	c.nr_seq_tipo_limitacao	= b.nr_sequencia
	and	((coalesce(a.cd_procedimento::text, '') = '') or (a.cd_procedimento = cd_procedimento_p))
	and	((coalesce(a.ie_origem_proced::text, '') = '') or (a.ie_origem_proced = ie_origem_proced_w))
	and	((coalesce(a.cd_grupo_proc::text, '') = '') or (a.cd_grupo_proc = cd_grupo_w))
	and	((coalesce(a.cd_especialidade::text, '') = '') or (a.cd_especialidade = cd_especialidade_w))
	and	((coalesce(a.cd_area_procedimento::text, '') = '') or (a.cd_area_procedimento = cd_area_w))
	and	coalesce(a.cd_doenca_cid::text, '') = ''
	and	coalesce(a.ie_tipo_guia::text, '') = ''
	and	b.ie_situacao	= 'A'
	and	c.nr_seq_plano	= nr_seq_plano_w
	and	ie_forma_limitacao_w = 'SCA'
	and	clock_timestamp() between coalesce(c.dt_inicio_vigencia,clock_timestamp()) and coalesce(c.dt_fim_vigencia,clock_timestamp())
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.nr_seq_local = nr_seq_local_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.nr_seq_dente = nr_seq_dente_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_distal = ie_face_distal_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_incisal = ie_face_incisal_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_lingual = ie_face_lingual_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_mesial = ie_face_mesial_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_oclusal = ie_face_oclusal_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_palatina = ie_face_palatina_p))
	and ((a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') or (a.ie_face_vestibular = ie_face_vestibular_p))
	and	c.nr_seq_plano		in (	select	t.nr_seq_plano
							from	pls_sca_vinculo	t
							where	t.nr_seq_segurado	= nr_seq_segurado_p
							and	(t.dt_liberacao IS NOT NULL AND t.dt_liberacao::text <> ''))
	order by nr_seq_grupo_servico,
		cd_area_procedimento,
		cd_especialidade,
		cd_grupo_proc,
		cd_procedimento,
		limita;

BEGIN

if (coalesce(nr_seq_guia_proc_p,0) > 0) then
	ie_tipo_consistencia_w	:= 'G';
elsif (coalesce(nr_seq_conta_proc_p,0) > 0) then
	ie_tipo_consistencia_w	:= 'C';
	
	if (nr_seq_reembolso_proc_p IS NOT NULL AND nr_seq_reembolso_proc_p::text <> '') then
		ie_tipo_consistencia_w := 'CR';
	end if;
	
elsif (coalesce(nr_seq_requisicao_proc_p,0)>0) then
	ie_tipo_consistencia_w	:= 'R';
end if;

pls_obter_estrut_proc(cd_procedimento_p, ie_origem_proced_p, cd_area_w,
	cd_especialidade_w, cd_grupo_w, ie_origem_proced_w);

begin
select	nr_seq_plano,
	nr_seq_contrato	
into STRICT	nr_seq_plano_w,
	nr_seq_contrato_w	
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;
exception
when others then
	nr_seq_plano_w		:= '';
	nr_seq_contrato_w	:= '';	
end;
if (ie_tipo_consistencia_w = 'G') then
	begin
	select	nr_seq_prestador,
		nr_seq_plano
	into STRICT	nr_seq_prestador_w,
		nr_seq_plano_w
	from	pls_guia_plano
	where	nr_sequencia = nr_seq_guia_p;
	exception
	when others then
		nr_seq_prestador_w	:= null;
		nr_seq_plano_w		:= '';
	end;
elsif (ie_tipo_consistencia_w in ('C','CR')) then
	select	nr_seq_conta
	into STRICT	nr_seq_conta_w
	from	pls_conta_proc
	where	nr_sequencia = nr_seq_conta_proc_p;
	
	begin
	select	a.nr_seq_prestador
	into STRICT	nr_seq_prestador_w
	from	pls_protocolo_conta a,
		pls_conta b
	where	a.nr_sequencia = b.nr_seq_protocolo
	and	b.nr_sequencia = nr_seq_conta_w;
	
	exception
	when others then
		nr_seq_prestador_w := null;
	end;
elsif (ie_tipo_consistencia_w = 'R') then
	begin
	select	b.nr_seq_prestador,
		b.nr_sequencia,
		b.nr_seq_plano
	into STRICT	nr_seq_prestador_w,
		nr_seq_requisicao_w,
		nr_seq_plano_w
	from	pls_requisicao		b,
		pls_requisicao_proc	a
	where	a.nr_seq_requisicao	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_requisicao_proc_p;
	exception
	when others then
		nr_seq_prestador_w	:= null;
		nr_seq_plano_w		:= '';
	end;
end if;

/* Francisco - 28/05/2012 - Mudei a forma de abrir o cursor, devido a performance */

ie_forma_limitacao_w	:= null;
select	count(1)
into STRICT	qt_limitacao_w
from	pls_limitacao x
where	x.nr_seq_contrato	= nr_seq_contrato_w
and 	clock_timestamp() between coalesce(x.dt_inicio_vigencia,clock_timestamp()) and coalesce(x.dt_fim_vigencia,clock_timestamp());

if (qt_limitacao_w = 0) then
	select	count(1)
	into STRICT	qt_limitacao_w
	from	pls_limitacao x
	where	clock_timestamp() between coalesce(x.dt_inicio_vigencia,clock_timestamp()) and coalesce(x.dt_fim_vigencia,clock_timestamp());
	
	if (qt_limitacao_w = 0) then
		select	count(1)
		into STRICT	qt_limitacao_w
		from	pls_limitacao x
		where	x.nr_seq_plano		= nr_seq_plano_w
		and 	clock_timestamp() between coalesce(x.dt_inicio_vigencia,clock_timestamp()) and coalesce(x.dt_fim_vigencia,clock_timestamp());
		
		if (qt_limitacao_w = 0) then
			select	count(1)
			into STRICT	qt_limitacao_w
			from	pls_limitacao x
			where	x.nr_seq_plano		in (	SELECT	t.nr_seq_plano
									from	pls_sca_vinculo	t
									where	t.nr_seq_segurado	= nr_seq_segurado_p
									and	(t.dt_liberacao IS NOT NULL AND t.dt_liberacao::text <> ''))
			and 	clock_timestamp() between coalesce(x.dt_inicio_vigencia,clock_timestamp()) and coalesce(x.dt_fim_vigencia,clock_timestamp());
			if (qt_limitacao_w > 0) then
				ie_forma_limitacao_w	:= 'SCA';
			end if;
		else
			ie_forma_limitacao_w	:= 'P';
		end if;
	else
		ie_forma_limitacao_w	:= 'I';
	end if;
else
	ie_forma_limitacao_w	:= 'C';
end if;

ie_lib_ant_w	:= 'N';
ie_liberado_w	:= 'N';
	
if (ie_forma_limitacao_w IS NOT NULL AND ie_forma_limitacao_w::text <> '') then
	open c01;
	loop
	fetch c01 into	
		nr_seq_limitacao_proc_w,
		ie_liberado_w,
		nr_seq_tipo_limitacao_w,
		qt_permitida_w,
		qt_meses_intervalo_w,
		ie_periodo_w,
		nr_seq_contrato_w,
		nr_seq_plano_w,
		ie_tipo_periodo_w,
		nr_seq_limitacao_w,
		nr_seq_grupo_servico_w,
		cd_area_procedimento_ww,
		cd_especialidade_ww,
		cd_grupo_proc_ww,
		cd_procedimento_ww,
		ie_ordem_limita_w,
		ie_qt_familia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		if (coalesce(ie_qt_familia_w,'N')	= 'S') then
			select	max(nr_seq_titular)
			into STRICT	nr_seq_titular_w
			from	pls_segurado
			where	nr_sequencia	= nr_seq_segurado_p
			and	ie_situacao_atend	= 'A'
			and	substr(pls_obter_dados_segurado(nr_sequencia,'CS'),1,10) = 'A';
			
			if (coalesce(nr_seq_titular_w::text, '') = '') then
				select	count(1)
				into STRICT	qt_pessoas_w
				from	pls_segurado
				where	nr_seq_titular	= nr_seq_segurado_p
				and	ie_situacao_atend	= 'A'
				and	substr(pls_obter_dados_segurado(nr_sequencia,'CS'),1,10) = 'A';
				
				-- Deve ser adicionado mais 1 para que seja considerado o titular.
				qt_pessoas_w 	:= qt_pessoas_w + 1;
			else
				select	count(1)
				into STRICT	qt_pessoas_w
				from	pls_segurado
				where	nr_seq_titular	= nr_seq_titular_w
				and	ie_situacao_atend	= 'A'
				and	substr(pls_obter_dados_segurado(nr_sequencia,'CS'),1,10) = 'A';
				
				-- Deve ser adicionado mais 1 para que seja considerado o titular.
				qt_pessoas_w 	:= qt_pessoas_w + 1;
			end if;
			
			qt_permitida_w	:=	qt_pessoas_w * qt_permitida_w;
		end if;
		
		if (nr_seq_grupo_servico_w IS NOT NULL AND nr_seq_grupo_servico_w::text <> '') then
			ie_serv_lib_w	:= pls_se_grupo_preco_servico_lib(nr_seq_grupo_servico_w,cd_procedimento_p,ie_origem_proced_p);
			if (ie_serv_lib_w = 'S') then
				ie_liberado_w	:= ie_liberado_w;
			else
				ie_liberado_w	:= ie_lib_ant_w;
			end if;
		end if;
		ie_lib_ant_w	:= ie_liberado_w;
	end loop;
	close c01;
end if;


if (ie_liberado_w = 'S') then
	ie_origem_w	:= 'C';
	if (nr_seq_plano_w IS NOT NULL AND nr_seq_plano_w::text <> '') then
		select	max(coalesce(ie_tipo_operacao,'B'))
		into STRICT	ie_tipo_operacao_w
		from	pls_plano
		where	nr_sequencia = nr_seq_plano_w;
		if (ie_tipo_operacao_w = 'A') then
			ie_origem_w	:= 'A';
		else
			ie_origem_w	:= 'P';
		end if;
	end if;


	if (ie_tipo_consistencia_w = 'G') then
		ie_limita_w	:= 	substr(pls_obter_se_limitacao(nr_seq_segurado_p, nr_seq_tipo_limitacao_w, qt_permitida_w,
						qt_meses_intervalo_w, ie_periodo_w, qt_solicitada_p,
						null, nr_seq_guia_p, 'Q',
					'P', ie_tipo_periodo_w,nr_seq_limitacao_w),1,1);

		if (ie_limita_w = 'S') then
			CALL pls_gravar_motivo_glosa('1423',null,nr_seq_guia_proc_p,null,'Procedimento com limitacao',nm_usuario_p,ie_origem_w,'CG',nr_seq_prestador_w, '(Regra ' || nr_seq_tipo_limitacao_w || ' -> ' || nr_seq_limitacao_proc_w || ')',null);
		
			update	pls_guia_plano_proc
			set	nr_seq_tipo_limitacao	= nr_seq_tipo_limitacao_w
			where	nr_sequencia		= nr_seq_guia_proc_p;
		end if;
	elsif (ie_tipo_consistencia_w = 'C') then
		
		pls_se_limitacao_conta(nr_seq_segurado_p, nr_seq_tipo_limitacao_w, qt_permitida_w,
					qt_meses_intervalo_w, ie_periodo_w, qt_solicitada_p,
					nr_seq_conta_proc_p, ie_limita_w, ie_tipo_periodo_w,
					nm_usuario_p, cd_estabelecimento_p);
				
		if (ie_limita_w = 'S') then	
			CALL pls_gravar_conta_glosa('1423',null, nr_seq_conta_proc_p,
						null,'N','Procedimento com limitacao',
						nm_usuario_p,'A','CC',
						nr_seq_prestador_w, cd_estabelecimento_p, '', null);
			update	pls_conta_proc
			set	nr_seq_tipo_limitacao	= nr_seq_tipo_limitacao_w
			where	nr_sequencia		= nr_seq_conta_proc_p;
		end if;
	elsif (ie_tipo_consistencia_w = 'R') then
		
		ie_limita_w	:=	substr(pls_obter_se_limitacao_req(nr_seq_segurado_p, nr_seq_tipo_limitacao_w, qt_permitida_w,
					qt_meses_intervalo_w, ie_periodo_w, qt_solicitada_p,null, nr_seq_requisicao_w, 'Q','P',
					ie_tipo_periodo_w,nr_seq_limitacao_w),1,1);
		if (ie_limita_w = 'S') then	
			CALL pls_gravar_requisicao_glosa('1423', null, nr_seq_requisicao_proc_p,
				null, 'Procedimento com limitacao', nm_usuario_p,
				nr_seq_prestador_w, cd_estabelecimento_p, null,
				'');

			update	pls_requisicao_proc
			set	nr_seq_tipo_limitacao	= nr_seq_tipo_limitacao_w
			where	nr_sequencia		= nr_seq_requisicao_proc_p;
		end if;
		
	elsif (ie_tipo_consistencia_w = 'CR') then
		
		pls_se_limitacao_conta(nr_seq_segurado_p, nr_seq_tipo_limitacao_w, qt_permitida_w,
					qt_meses_intervalo_w, ie_periodo_w, qt_solicitada_p,
					nr_seq_conta_proc_p, ie_limita_w, ie_tipo_periodo_w,
					nm_usuario_p, cd_estabelecimento_p);
				
		if (ie_limita_w = 'S') then	
			CALL pls_gravar_conta_glosa('1423',null, nr_seq_conta_proc_p,
						null,'N','Procedimento com limitacao',
						nm_usuario_p,'A','CR',
						nr_seq_prestador_w, cd_estabelecimento_p, '', null);
			update	pls_conta_proc
			set	nr_seq_tipo_limitacao	= nr_seq_tipo_limitacao_w
			where	nr_sequencia		= nr_seq_conta_proc_p;
		end if;
	end if;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_odonto_pck.validar_limitacao_proc ( nr_seq_segurado_p bigint, nr_seq_guia_proc_p bigint, nr_seq_conta_proc_p bigint, nr_seq_reembolso_proc_p bigint, nr_seq_requisicao_proc_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, qt_solicitada_p bigint, nr_seq_guia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_local_p text, nr_seq_dente_p text, ie_face_distal_p text, ie_face_incisal_p text, ie_face_lingual_p text, ie_face_mesial_p text, ie_face_oclusal_p text, ie_face_palatina_p text, ie_face_vestibular_p text) FROM PUBLIC;
