SET client_encoding TO 'UTF8';


--Apenas faz os updates para ajuste de diverg_ncia de distribuicao de valores das taxas para os registros filhos de taxa_fat



CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.ajusta_diverg_fat_adm_tx_pos ( tb_seq_item_p INOUT pls_util_cta_pck.t_number_table, tb_valor_ajuste_p INOUT pls_util_cta_pck.t_number_table, ie_tipo_item_p text) AS $body$
BEGIN

	if (tb_seq_item_p.count > 0) then
	
		if (ie_tipo_item_p = 'P') then
		
			forall i in tb_seq_item_p.first..tb_seq_item_p.last									
				update	pls_conta_pos_proc_tx_fat
				set 	vl_taxa		= coalesce(vl_taxa,0) + tb_valor_ajuste_p(i)
				where	nr_sequencia 	= tb_seq_item_p(i);
		
		else
			forall i in tb_seq_item_p.first..tb_seq_item_p.last
				update	pls_conta_pos_mat_tx_fat
				set 	vl_taxa		= coalesce(vl_taxa,0) + tb_valor_ajuste_p(i)
				where	nr_sequencia 	= tb_seq_item_p(i);
		
		end if;
		commit;
	end if;
	tb_seq_item_p.delete;
	tb_valor_ajuste_p.delete;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
