SET client_encoding TO 'UTF8';


 --atualiza a informacao de cobran_a de CO e filme nas tabelas tempor_rias.



CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.atualiza_cobranca_co_filme ( tb_seq_item_p INOUT pls_util_cta_pck.t_number_table, tb_cobra_co_p INOUT pls_util_cta_pck.t_varchar2_table_1, tb_cobra_filme_p INOUT pls_util_cta_pck.t_varchar2_table_1, tb_seq_regra_p INOUT pls_util_cta_pck.t_number_table) AS $body$
BEGIN
	--Se a lista n_o estiver vazia, ent_o faz a atualizacao.

	if ( tb_seq_item_p.count > 0) then
		forall i in tb_seq_item_p.first..tb_seq_item_p.last
			update w_pls_conta_pos_proc
			set	ie_cobrar_co 			= tb_cobra_co_p(i),
				ie_cobrar_filme 		= tb_cobra_filme_p(i),
				nr_seq_regra_co_filme		= tb_seq_regra_p(i),
				vl_taxa_material		= CASE WHEN tb_cobra_filme_p(i)='N' THEN  0  ELSE vl_taxa_material END ,
				vl_taxa_co			= CASE WHEN tb_cobra_co_p(i)='N' THEN  0  ELSE vl_taxa_co END ,
				vl_materiais_calc		= CASE WHEN tb_cobra_filme_p(i)='N' THEN  0  ELSE vl_materiais_calc END ,
				vl_custo_operacional_calc	= CASE WHEN tb_cobra_co_p(i)='N' THEN  0  ELSE vl_custo_operacional_calc END
			where	nr_sequencia 			= tb_seq_item_p(i);
	end if;
	tb_seq_item_p.delete;
	tb_cobra_co_p.delete;
	tb_cobra_filme_p.delete;
	tb_seq_regra_p.delete;	
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
