--Apenas atualiza as convers_es na tabela tempor_ria.



CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.atualiza_conversoes_mat ( tb_seq_item_p INOUT pls_util_cta_pck.t_number_table, tb_ds_item_conv_p INOUT pls_util_cta_pck.t_varchar2_table_255, tb_regra_p INOUT pls_util_cta_pck.t_number_table, tb_cd_item_conv_p INOUT pls_util_cta_pck.t_varchar2_table_10) AS $body$
BEGIN

	if ( tb_seq_item_p.count > 0) then

		forall i in tb_seq_item_p.first..tb_seq_item_p.last
			update	w_pls_conta_pos_mat
			set	nr_seq_regra_conv	= tb_regra_p(i),
				ds_item_convertido	= tb_ds_item_conv_p(i),
				cd_item_convertido	= tb_cd_item_conv_p(i)
			where	nr_sequencia = tb_seq_item_p(i);
			
			
	end if;
	
	tb_seq_item_p.delete;
        tb_ds_item_conv_p.delete;
        tb_regra_p.delete;
	tb_cd_item_conv_p.delete;
	
END;

$body$
