--Rotina respons_vel pela atualizacao de valor de p_s e tx_adm dos participantes para resolucao de diverg_ncias.



CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.atualiza_dif_participantes ( tb_seq_conta_proc_w INOUT pls_util_cta_pck.t_number_table, tb_vl_dif_pos_w INOUT pls_util_cta_pck.t_number_table, tb_vl_dif_taxa_w INOUT pls_util_cta_pck.t_number_table, tb_maior_valor_w INOUT pls_util_cta_pck.t_number_table) AS $body$
BEGIN

	if ( tb_seq_conta_proc_w.count > 0) then
	
		forall i in tb_seq_conta_proc_w.first..tb_seq_conta_proc_w.last
			update	pls_conta_pos_proc_part
			set 	vl_administracao = vl_administracao + tb_vl_dif_taxa_w(i),
				vl_participante_pos = vl_participante_pos + tb_vl_dif_pos_w(i)
			where	nr_seq_conta_pos_proc = tb_seq_conta_proc_w(i)
			and 	vl_participante_pos + tb_vl_dif_pos_w(i) > 0;
	end if;
	
	tb_seq_conta_proc_w.delete;
	tb_vl_dif_pos_w.delete;
	tb_vl_dif_taxa_w.delete;
	tb_maior_valor_w.delete;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
