--Atualiza cada item das tabelas tempor_rias para identificar se deve ou n_o gerar p_s_estabelecido.



CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.atualiza_item_gera_pos_estab ( tb_seq_item_p pls_util_cta_pck.t_number_table, tb_gera_pos_p pls_util_cta_pck.t_varchar2_table_5, tb_congenere_p pls_util_cta_pck.t_number_table, ie_tipo_item_p text) AS $body$
BEGIN
	if ( tb_seq_item_p.count > 0) then
	
		if (ie_tipo_item_p = 'P') then
			forall i in tb_seq_item_p.first..tb_seq_item_p.last
				update	w_pls_conta_pos_proc
				set 	ie_gera_valor_pos_estab = tb_gera_pos_p(i),
					nr_seq_congenere 	= tb_congenere_p(i)
				where	nr_sequencia 		= tb_seq_item_p(i);
		else
			forall i in tb_seq_item_p.first..tb_seq_item_p.last
				update	w_pls_conta_pos_mat
				set 	ie_gera_valor_pos_estab = tb_gera_pos_p(i),
					nr_seq_congenere	= tb_congenere_p(i)
				where	nr_sequencia 		= tb_seq_item_p(i);
		end if;
	end if;

END;

$body$
