--Atualiza os registros de p_s para participantes.



CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.atualiza_partic_pos ( tb_seq_proc_partic INOUT pls_util_cta_pck.t_number_table, tb_seq_conta_proc INOUT pls_util_cta_pck.t_number_table, tb_vl_pos_estab INOUT pls_util_cta_pck.t_number_table, tb_vl_administracao INOUT pls_util_cta_pck.t_number_table, tb_seq_pos_proc_part INOUT pls_util_cta_pck.t_number_table, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN
	if (tb_seq_proc_partic.count > 0) then
	
		forall i in tb_seq_proc_partic.first..tb_seq_proc_partic.last
			update	pls_conta_pos_proc_part
			set	vl_administracao 	= tb_vl_administracao(i),
				vl_participante_pos 	= tb_vl_pos_estab(i),
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
			where	nr_seq_proc_partic	= tb_seq_proc_partic(i);
	
		commit;
	end if;
	tb_seq_pos_proc_part.delete;
	tb_seq_proc_partic.delete;
	tb_seq_conta_proc.delete;
	tb_vl_pos_estab.delete;
	tb_vl_administracao.delete;

END;

$body$
