--efetiva a atualizacao do valor de provis_o da pls_conta_pos_mat_contab



CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.atualiza_prov_mat_ctb ( tb_seq_ctb_p INOUT pls_util_cta_pck.t_number_table, tb_vl_ajuste_p INOUT pls_util_cta_pck.t_number_table, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

	if ( tb_seq_ctb_p.count > 0) then
	
		forall i in tb_seq_ctb_p.first..tb_seq_ctb_p.last
			update	pls_conta_pos_mat_contab
			set	vl_provisao = vl_provisao + tb_vl_ajuste_p(i)
			where	nr_sequencia = tb_seq_ctb_p(i)
			and	vl_provisao + tb_vl_ajuste_p(i) >= 0;
	
	end if;
	tb_seq_ctb_p.delete;
	tb_vl_ajuste_p.delete;
	
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
