--Atualiza informacao de tipo de tabela base para valorizacao, se faturamento ou interc_mbio cobran_a



CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.atualiza_tipo_tab_base ( tb_seq_item_p INOUT pls_util_cta_pck.t_number_table, tb_base_p INOUT pls_util_cta_pck.t_varchar2_table_2, tb_tipo_item_p INOUT pls_util_cta_pck.t_varchar2_table_1 ) AS $body$
BEGIN
	-- Pode atualizar tanto a tabela material quanto a procedimento pois controlar_ pelo tipo de item passado por par_metro

	if (tb_seq_item_p.count > 0) then
		
		forall i in tb_seq_item_p.first..tb_seq_item_p.last
			update	w_pls_conta_pos_proc
			set	ie_tipo_tabela_valorizacao = tb_base_p(i)
			where	nr_sequencia = tb_seq_item_p(i)
			and	tb_tipo_item_p(i) = 'P';
			
		forall i in tb_seq_item_p.first..tb_seq_item_p.last
			update	w_pls_conta_pos_mat
			set	ie_tipo_tabela_valorizacao = tb_base_p(i)
			where	nr_sequencia = tb_seq_item_p(i)
			and	tb_tipo_item_p(i) = 'M';
	end if;

	tb_seq_item_p.delete;
	tb_base_p.delete;
	tb_tipo_item_p.delete;
	
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
