--Apenas faz o update em massa dos valores com interc_mbio dos registros de procedimentos



CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.atualiza_tx_inter_proc ( tb_seq_item_w INOUT pls_util_cta_pck.t_number_table, tb_materiais_w INOUT pls_util_cta_pck.t_number_table, tb_medico_w INOUT pls_util_cta_pck.t_number_table, tb_co_w INOUT pls_util_cta_pck.t_number_table) AS $body$
BEGIN

	if ( tb_seq_item_w.count > 0) then
		
		forall i in tb_seq_item_w.first..tb_seq_item_w.last
			update	w_pls_conta_pos_proc
			set	vl_taxa_material= tb_materiais_w(i),
				vl_taxa_servico	= tb_medico_w(i),
				vl_taxa_co	= tb_co_w(i)
			where	nr_sequencia	= tb_seq_item_w(i);
	
	end if;
	tb_seq_item_w.delete;
	tb_materiais_w.delete;
	tb_medico_w.delete;
	tb_co_w.delete;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
