CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.atualiza_val_individuais_proc ( tb_seq_item_p INOUT pls_util_cta_pck.t_number_table, tb_vl_medico_p INOUT pls_util_cta_pck.t_number_table, tb_vl_materiais_p INOUT pls_util_cta_pck.t_number_table, tb_vl_custo_operacional_p INOUT pls_util_cta_pck.t_number_table ) AS $body$
BEGIN
	if (tb_seq_item_p.count > 0) then
	
		forall i in tb_seq_item_p.first..tb_seq_item_p.last
			update	w_pls_conta_pos_proc
			set	vl_custo_operacional_calc	= tb_vl_custo_operacional_p(i),
				vl_medico_calc				= tb_vl_medico_p(i),
				vl_materiais_calc			= tb_vl_materiais_p(i)
			where	nr_sequencia			= tb_seq_item_p(i);
	end if;
	
	tb_seq_item_p.delete;
	tb_vl_medico_p.delete;
	tb_vl_materiais_p.delete;
	tb_vl_custo_operacional_p.delete;
	
END;

$body$
