--Atualiza os valores de p_s nos itens selecionados



CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.atualiza_vl_beneficiario_itens ( tb_vl_benef_p INOUT pls_util_cta_pck.t_number_table, tb_seq_item_p INOUT pls_util_cta_pck.t_number_table, ie_tipo_item_p text) AS $body$
BEGIN

	if ( tb_seq_item_p.count > 0) then
		
		if ( ie_tipo_item_p = 'P') then
			forall i in tb_seq_item_p.first..tb_seq_item_p.last
				update 	pls_conta_proc
				set	vl_beneficiario = tb_vl_benef_p(i)
				where 	nr_sequencia	= tb_seq_item_p(i);
		else
			forall i in tb_seq_item_p.first..tb_seq_item_p.last
				update 	pls_conta_mat
				set	vl_beneficiario = tb_vl_benef_p(i)
				where 	nr_sequencia	= tb_seq_item_p(i);
		end if;
		
		commit;
		
	end if;
	tb_seq_item_p.delete;
	tb_vl_benef_p.delete;
	
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
