CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.atualizar_reg_ptu_mat ( tb_nr_sequencia_upd_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_conta_upd_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_conta_mat_pos_upd_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_conta_resumo_upd_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_prestador_pgto_upd_p INOUT pls_util_cta_pck.t_number_table, tb_vl_administracao_upd_p INOUT pls_util_cta_pck.t_number_table, tb_vl_materiais_upd_p INOUT pls_util_cta_pck.t_number_table, tb_dt_mes_p INOUT pls_util_cta_pck.t_date_table, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN
	if ( tb_nr_sequencia_upd_p.count > 0) then
	
		forall i in tb_nr_sequencia_upd_p.first..tb_nr_sequencia_upd_p.last
			update	pls_conta_pos_mat_fat
			set	nr_seq_conta		= tb_nr_seq_conta_upd_p(i),
				nr_seq_conta_mat_pos	= tb_nr_seq_conta_mat_pos_upd_p(i),
				nr_seq_conta_resumo	= tb_nr_seq_conta_resumo_upd_p(i),
				nr_seq_prestador_pgto	= tb_nr_seq_prestador_pgto_upd_p(i),
				vl_administracao	= tb_vl_administracao_upd_p(i),
				vl_lib_taxa_material	= tb_vl_administracao_upd_p(i),
				vl_materiais		= tb_vl_materiais_upd_p(i),
				vl_pos_estabelecido	= tb_vl_materiais_upd_p(i),
				dt_mes_competencia	= tb_dt_mes_p(i),
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
			where	nr_sequencia		= tb_nr_sequencia_upd_p(i);
	end if;
	tb_nr_sequencia_upd_p.delete;
	tb_nr_seq_conta_upd_p.delete;
	tb_nr_seq_conta_mat_pos_upd_p.delete;
	tb_nr_seq_conta_resumo_upd_p.delete;
	tb_nr_seq_prestador_pgto_upd_p.delete;
	tb_vl_administracao_upd_p.delete;
	tb_vl_materiais_upd_p.delete;
	tb_dt_mes_p.delete;
	
END;

$body$
