--Cria os registros de p_s para participantes.



CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.insere_partic_pos ( tb_seq_proc_partic INOUT pls_util_cta_pck.t_number_table, tb_seq_conta_proc INOUT pls_util_cta_pck.t_number_table, tb_vl_pos_estab INOUT pls_util_cta_pck.t_number_table, tb_vl_administracao INOUT pls_util_cta_pck.t_number_table, tb_seq_pos_proc_part INOUT pls_util_cta_pck.t_number_table, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN
	if ( tb_seq_proc_partic.count > 0) then
		
		forall i in tb_seq_proc_partic.first..tb_seq_proc_partic.last
			insert into pls_conta_pos_proc_part( nr_sequencia, vl_administracao, vl_participante_pos,
				  nr_seq_proc_partic, nr_seq_conta_pos_proc, nm_usuario,
				  nm_usuario_nrec, dt_atualizacao, dt_atualizacao_nrec)
			values ( tb_seq_pos_proc_part(i), tb_vl_administracao(i), tb_vl_pos_estab(i),
				  tb_seq_proc_partic(i), tb_seq_conta_proc(i), nm_usuario_p,
				  nm_usuario_p, clock_timestamp(), clock_timestamp());
	
	end if;
	tb_seq_pos_proc_part.delete;
	tb_seq_proc_partic.delete;
	tb_seq_conta_proc.delete;
	tb_vl_pos_estab.delete;
	tb_vl_administracao.delete;

END;

$body$
