CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.limpa_reg_taxas_sem_tx_ctb ( tb_nr_seq_conta_pos_estab_p pls_util_cta_pck.t_number_table, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

	if (tb_nr_seq_conta_pos_estab_p.count > 0) then
	
		forall i in tb_nr_seq_conta_pos_estab_p.first..tb_nr_seq_conta_pos_estab_p.last
			delete 	FROM pls_conta_pos_mat_tx a
			where 	a.nr_seq_conta_mat_pos in (	SELECT	b.nr_sequencia
								from	pls_conta_pos_mat b
								where	b.nr_seq_conta = tb_nr_seq_conta_pos_estab_p(i))						
			and	not exists ( 	SELECT	1
						from	pls_conta_pos_mat_tx_ctb x
						where	x.nr_seq_pos_mat_tx	= a.nr_sequencia);
		
		forall i in tb_nr_seq_conta_pos_estab_p.first..tb_nr_seq_conta_pos_estab_p.last
			delete 	FROM pls_conta_pos_proc_tx a
			where 	a.nr_seq_conta_pos_proc in (	SELECT	b.nr_sequencia
								from	pls_conta_pos_proc b
								where	b.nr_seq_conta = tb_nr_seq_conta_pos_estab_p(i))
			and	not exists (select	1
						from	pls_conta_pos_proc_tx_ctb x
						where	x.nr_seq_proc_pos_tx	= a.nr_sequencia);
						
		commit;
	end if;

END;

$body$
