--Faz as verificacaes e eventuais convers_es de procedimentos conforme as respectivas regras



CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.processa_conv_materiais ( nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


_ora2pg_r RECORD;
i			integer := 0;
qt_regras_w		integer := 0;
ie_tipo_tabela_conv_w	smallint;
nr_seq_material_ret_w	pls_conversao_mat.nr_seq_material%type;
ds_item_convertido_w	pls_conversao_mat.ds_material_envio%type;
cd_item_convertido_w	pls_conversao_mat.cd_material_envio%type;
nr_seq_regra_conv_w	w_pls_conta_pos_mat.nr_seq_regra_conv%type;
tb_seq_item_w		pls_util_cta_pck.t_number_table;
tb_regra_w		pls_util_cta_pck.t_number_table;
tb_cd_item_conv_w	pls_util_cta_pck.t_varchar2_table_10;
tb_ds_item_conv_w	pls_util_cta_pck.t_varchar2_table_255;

					
C01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_tipo_despesa,
		nr_seq_prestador_exec,
		( 	SELECT max(cd_material_ops)
			from	pls_material
			where	nr_sequencia = mat.nr_seq_material) cd_material_ops,
		(	select	max(nr_seq_contrato)
			from	pls_segurado
			where	nr_sequencia = mat.nr_seq_segurado)	nr_seq_contrato,
		(	select 	max(nr_seq_oper_congenere)
			from 	pls_intercambio
			where	nr_sequencia = mat.nr_seq_intercambio) nr_seq_ops_congenere
	from	w_pls_conta_pos_mat 	mat
	where	mat.ie_gera_valor_pos_estab in ('S', 'SB');
					
BEGIN
	select count(1)
	into STRICT	qt_regras_w
	from	pls_conversao_mat
	where 	ie_envio_receb = 'E';

	--Se n_o tiver regras de envio, nem precisa processar nada aqui.

	if ( qt_regras_w > 0) then
		
		--Essa primeira chamada _ apenas para estartar as estruturas com valores nulos.

		SELECT * FROM pls_pos_estabelecido_pck.atualiza_conversoes_mat( 	tb_seq_item_w, tb_ds_item_conv_w, tb_regra_w, tb_cd_item_conv_w) INTO STRICT _ora2pg_r;
  	tb_seq_item_w := _ora2pg_r.tb_seq_item_p; tb_ds_item_conv_w := _ora2pg_r.tb_ds_item_conv_p; tb_regra_w := _ora2pg_r.tb_regra_p; tb_cd_item_conv_w := _ora2pg_r.tb_cd_item_conv_p;
		for r_c01_w in C01 loop
		
			if (r_c01_w.cd_material_ops IS NOT NULL AND r_c01_w.cd_material_ops::text <> '') then
				pls_obter_mat_conversao(r_c01_w.cd_material_ops,
							r_c01_w.ie_tipo_despesa,
							nm_usuario_p,
							'E',
							r_c01_w.nr_seq_contrato,
							r_c01_w.nr_seq_ops_congenere,
							r_c01_w.nr_seq_prestador_exec,
							nr_seq_material_ret_w,
							cd_item_convertido_w,
							ds_item_convertido_w,
							ie_tipo_tabela_conv_w,
							nr_seq_regra_conv_w,
							null);
			end if;
			
			tb_seq_item_w(i)	:= r_c01_w.nr_sequencia;
			tb_ds_item_conv_w(i)	:= ds_item_convertido_w;
			tb_regra_w(i)		:= nr_seq_regra_conv_w;
			tb_cd_item_conv_w(i)	:= cd_item_convertido_w;
			
			if (i > pls_util_cta_pck.qt_registro_transacao_w) then
				
				SELECT * FROM pls_pos_estabelecido_pck.atualiza_conversoes_mat( tb_seq_item_w, tb_ds_item_conv_w, tb_regra_w, tb_cd_item_conv_w) INTO STRICT _ora2pg_r;
  tb_seq_item_w := _ora2pg_r.tb_seq_item_p; tb_ds_item_conv_w := _ora2pg_r.tb_ds_item_conv_p; tb_regra_w := _ora2pg_r.tb_regra_p; tb_cd_item_conv_w := _ora2pg_r.tb_cd_item_conv_p;
				i := 0;
			else
			
				i := i + 1;
			end if;
	
				
	
		end loop;
		
		--Se sobraram registros nas estruturas, atualiza no banco

		
	
		SELECT * FROM pls_pos_estabelecido_pck.atualiza_conversoes_mat( tb_seq_item_w, tb_ds_item_conv_w, tb_regra_w, tb_cd_item_conv_w) INTO STRICT _ora2pg_r;
  tb_seq_item_w := _ora2pg_r.tb_seq_item_p; tb_ds_item_conv_w := _ora2pg_r.tb_ds_item_conv_p; tb_regra_w := _ora2pg_r.tb_regra_p; tb_cd_item_conv_w := _ora2pg_r.tb_cd_item_conv_p;
	end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
