CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.upd_atualiza_vl_tx_fat ( tb_vl_fat_taxa_p INOUT pls_util_cta_pck.t_number_table, tb_cta_fat_taxa_p INOUT pls_util_cta_pck.t_number_table, nm_usuario_p usuario.nm_usuario%type, ie_tipo_item_p text) AS $body$
BEGIN

	if (tb_vl_fat_taxa_p.count > 0) then
	
		if ( ie_tipo_item_p = 'P') then
		
			forall i in tb_vl_fat_taxa_p.first..tb_vl_fat_taxa_p.last
				update	pls_conta_pos_proc_tx_fat
				set	vl_taxa		= tb_vl_fat_taxa_p(i)
				where	nr_sequencia	= tb_cta_fat_taxa_p(i);
		
		else
			forall i in tb_vl_fat_taxa_p.first..tb_vl_fat_taxa_p.last
				update	pls_conta_pos_mat_tx_fat
				set	vl_taxa		= tb_vl_fat_taxa_p(i)
				where	nr_sequencia	= tb_cta_fat_taxa_p(i);
			
		end if;
		commit;
	end if;
	tb_vl_fat_taxa_p.delete;
	tb_cta_fat_taxa_p.delete;

END;		

$body$
