CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.upd_proc_tx_opme ( tb_seq_proc_upd_p INOUT pls_util_cta_pck.t_number_table, tb_dt_atend_ref_upd_p INOUT pls_util_cta_pck.t_date_table, tb_lib_taxa_material_p INOUT pls_util_cta_pck.t_number_table, tb_vl_materiais_p INOUT pls_util_cta_pck.t_number_table, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

	if ( tb_seq_proc_upd_p.count > 0 ) then
	
		forall i in tb_seq_proc_upd_p.first..tb_seq_proc_upd_p.last
			update 	pls_conta_proc
			set	dt_inicio_proc 	= tb_dt_atend_ref_upd_p(i),
				dt_fim_proc	= tb_dt_atend_ref_upd_p(i),
				dt_procedimento	= tb_dt_atend_ref_upd_p(i),
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
			where	nr_sequencia = tb_seq_proc_upd_p(i);
			
		forall i in tb_seq_proc_upd_p.first..tb_seq_proc_upd_p.last
			update	pls_conta_pos_proc
			set	vl_lib_taxa_material 	= tb_lib_taxa_material_p(i),
				vl_materiais		= tb_vl_materiais_p(i),
				vl_provisao		= tb_vl_materiais_p(i) + tb_lib_taxa_material_p(i),
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
			where	nr_seq_conta_proc 	= tb_seq_proc_upd_p(i);	
	end if;	
	tb_lib_taxa_material_p.delete;
	tb_vl_materiais_p.delete;
	tb_seq_proc_upd_p.delete;

END;

$body$
