--Faz atualizacao de informacaes em registros de p_s-estabelecido.



CREATE OR REPLACE PROCEDURE pls_pos_estabelecido_pck.update_itens_pos ( tb_seq_proc_p INOUT pls_util_cta_pck.t_number_table, tb_seq_proc_ocor_p INOUT pls_util_cta_pck.t_number_table, tb_seq_mat_p INOUT pls_util_cta_pck.t_number_table, tb_seq_mat_ocor_p INOUT pls_util_cta_pck.t_number_table, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

	if (tb_seq_proc_p.count > 0) then
	
		forall i in tb_seq_mat_p.first..tb_seq_mat_p.last
			update	pls_ocorrencia_benef
			set	ie_situacao		= 'A',
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp(),
				ie_forma_inativacao	 = NULL
			where	nr_seq_conta_pos_estab	= tb_seq_proc_p(i)
			and	nr_seq_ocorrencia	= tb_seq_proc_ocor_p(i)
			and (coalesce(ie_forma_inativacao::text, '') = '' or ie_forma_inativacao = 'S');
		
		forall i in tb_seq_mat_p.first..tb_seq_mat_p.last
			update	pls_ocorrencia_benef
			set	ie_situacao		= 'I',
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp(),
				ie_forma_inativacao	= 'U'
			where	nr_seq_conta_pos_estab	= tb_seq_proc_p(i)
			and	nr_seq_ocorrencia	= tb_seq_proc_ocor_p(i)
			and	ie_forma_inativacao	= 'US';
	
	end if;
	
	if (tb_seq_mat_p.count > 0) then
	
		forall i in tb_seq_mat_p.first..tb_seq_mat_p.last
			update	pls_ocorrencia_benef
			set	ie_situacao		= 'A',
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp(),
				ie_forma_inativacao	 = NULL
			where	nr_seq_conta_pos_mat	= tb_seq_mat_p(i)
			and	nr_seq_ocorrencia	= tb_seq_mat_ocor_p(i)
			and (coalesce(ie_forma_inativacao::text, '') = '' or ie_forma_inativacao = 'S');

		forall i in tb_seq_mat_p.first..tb_seq_mat_p.last
			update	pls_ocorrencia_benef
			set	ie_situacao		= 'I',
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp(),
				ie_forma_inativacao	= 'U'
			where	nr_seq_conta_pos_mat	= tb_seq_mat_p(i)
			and	nr_seq_ocorrencia	= tb_seq_mat_ocor_p(i)
			and	ie_forma_inativacao 	= 'US';
	
	end if;
	tb_seq_proc_p.delete;
	tb_seq_proc_ocor_p.delete;
	tb_seq_mat_p.delete;
	tb_seq_mat_ocor_p.delete;

END;

$body$
