CREATE OR REPLACE PROCEDURE pls_pp_apropriacao_pck.baixar_apropriacoes_pagas ( nr_seq_lote_p pls_pp_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;

c01 CURSOR(	nr_seq_lote_pc	pls_pp_lote.nr_sequencia%type) FOR
	SELECT	a.nr_seq_pp_it_aprop
	from	pls_pp_item_lote a,
		pls_pp_item_apropriacao b
	where	a.nr_seq_lote = nr_seq_lote_pc
	and	a.ie_acao_negativo = 'N'
	and	b.nr_sequencia = a.nr_seq_pp_it_aprop
	and	b.ie_baixado = 'N';


BEGIN
-- retorna todas as apropriações que entraram neste lote de pagamento e que foram 'quitadas' neste lote
-- essa rotina é chamada após a definição de quais valores precisaram ser apropriados ou gerados títulos
open c01(nr_seq_lote_p);
loop
	fetch c01 bulk collect into	tb_nr_sequencia_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;
	
	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last
		update	pls_pp_item_apropriacao
		set	ie_baixado = 'S',
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;
close c01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
